/**
 * Created by Lenovo on 2019/10/14.
 */
/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import {Title} from '../../share';
import {getData, goNext} from '../../utils';
export default class AddressManage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            messageList:[
                {
                    consigneeName:"张三",
                    consigneeMobile:"18209603995",
                    consigneeAddress:"上海上海市",
                    addressDetail:"浦东新区金牛路1号1001室",
                    isDefaultAddress:2
                },{
                    consigneeName:"李四",
                    consigneeMobile:"18209603995",
                    consigneeAddress:"湖北省武汉市",
                    addressDetail:"浦东新区金牛路1号1001室",
                    isDefaultAddress:1
                }
            ]//初始化聊天列表
        }
    }
    componentDidMount(){
        this.getAddressList();
    }
    //获取聊天列表内容
    getAddressList(){
        //debugger;

        getData({
            method: 'get',
            url: 'getAddressList',
            data: {
                //userId:
            },
            successCB: (res) => {
                console.log(res);
                this.setState({
                    messageList:res.result
                })
            }
        })
    }
    render() {
        return <div>
            <Title title="我的收货地址" that={this} rightTitle="添加" onRightClick={()=>goNext(this,'addAddress')}/>
            <div className="cm-mlr-018">
                {this.state.messageList.length>0?this.state.messageList.map((item,index)=>{
                        return(
                            <div className="cm-flex cm-ai-c" key={index}>
                                <div className="cm-flex cm-ai-c cm-jc-sb cm-flex-1 cm-border-bottom-ddd cm-ptb-018">
                                    <div className="cm-flex-1 cm-mr-018">
                                        <div className="cm-c-333 cm-fs-028 cm-fw-bold"><span>{item.consigneeName}
                                        </span><span className="cm-c-999 cm-fs-026"> {item.consigneeMobile}</span></div>
                                        <div className="cm-c-666 cm-fs-026 cm-mt-009">{item.isDefaultAddress === 2?<span className="cm-p-006 cm-c-white" style={{background:"pink"}}>默认</span>:""}<span>{item.consigneeAddress}</span><span>{item.addressDetail}</span></div>
                                    </div>
                                    <div className="cm-btn-border-333" onClick={()=>goNext(this,'addAddress',item)}>编辑</div>
                                </div>
                            </div>
                        )
                    }):<div className="cm-tx-c cm-ptb-018 cm-border-bottom-ddd cm-c-666" onClick={()=>goNext(this,'addAddress')}>添加地址</div>}
            </div>
        </div>
    }
}
