import React from 'react';
import Index from '../index/index';
import Publish from '../publish/publish';
import Personal from '../personal/personal';
import MessageList from '../message/messageList';
import Login from '../login/login';
import myAct from '../../images/app/my.png';
import my from '../../images/app/my-gray.png';
import msgAct from '../../images/app/message.png';
import msg from '../../images/app/message-gray.png';
import index from '../../images/app/index-gray.png';
import indexAct from '../../images/app/index.png';
import publish from '../../images/app/pubilsh-gray.png';
import publishAct from '../../images/app/publish.png';
import {isDefine} from '../../utils';
import './App.css';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        let fourth = Personal;
        let user = localStorage.getItem("userInfo");
        if(!isDefine(user)){
            console.log(user);
            fourth = Login;
        }
        this.state = {
            children:[
                {label:"首页",icon:index,iconAct:indexAct,child:Index,path:""},
                {label:"发布",icon:publish,iconAct:publishAct,child:Publish,path:""},
                {label:"消息",icon:msg,iconAct:msgAct,child:MessageList,path:""},
                {label:"我的",icon:my,iconAct:myAct,child:fourth,path:""},
            ],
            activeKey:this.props.location.state||0
        }
    }
    changeTab(index){
        console.log(this);
        this.setState({
            activeKey:index
        })

    }
    componentWillReceiveProps(props){
        this.setState({
            activeKey:props.location.state
        })
    }
    render() {
        const {goHome} = this.props;
        return (
            <div>
                <div className="app-tabs">
                    {this.state.children.map((item,index)=>{
                        return(<div key={index} className={index == this.state.activeKey?"app-tab-active":"app-tab"} onClick={()=>this.changeTab(index)}>
                            <img className="app-icon" src={index == this.state.activeKey?item.iconAct:item.icon} alt=""/>
                            <span className="app-span">{item.label}</span>
                        </div>)
                    })}
                </div>
                {this.state.children.map((item,index)=>{
                    if(index == this.state.activeKey){
                        return(<div key={index}>{<item.child  history={this.props.history}></item.child>}</div>)
                    }else {
                        return null
                    }
                })}
            </div>
        )
    }
}
