/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './register.css';
import {Title,List,Input} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast,ImagePicker} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
import defaultAvatar from '../../images/login/avatar.jpg';
const Item = List.Item;
export default class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userAvatar:[],
            getCode:'获取验证码',
            userName: '',
            mobile: '',
            verifyCode:"",
            password:'',
            confirmPassword:'',
        }
    }
    goBack(){
        goNext(this,'login')
    }
    change(state){
        console.log(state);
        this.setState(state)
    }
    goLogin(){
        goNext(this,'login')
    }
    getVerify(){
        console.log(this.state.mobile);
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:1
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode == 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-small"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify()}>{this.state.getCode}</span></div>
        )
    }
    register(){
        var arr = [
            {value:this.state.userAvatar,msg:"请选择头像"},
            {value:this.state.userName,msg:"请输入用户名"},
            {value:this.state.mobile,msg:"请输入手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
            {value:this.state.password,msg:"请输入密码"},
            {value:this.state.confirmPassword,msg:"请输入确认密码"},
            {value:this.state.password == this.state.confirmPassword,msg:"密码与确认密码不一致"},
        ]
        checkParam(arr,()=> {
            getData({
                method:'post',
                url:'register',
                data:{
                    userAvatar:this.state.userAvatar,
                    userName:this.state.userName,
                    mobile:this.state.mobile,
                    verifyCode:this.state.verifyCode,
                    password:this.state.password,
                    confirmPassword:this.state.confirmPassword
                },
                successCB:(res)=> {
                    Toast.success(res.msg,1);
                    getData({
                        method:'post',
                        url:'login',
                        data:{
                            mobile:this.state.mobile,
                            password:this.state.password
                        },
                        successCB:(res)=> {
                            localStorage.setItem("userInfo",JSON.stringify(res));
                            goNext(this,'app',0)
                        }
                    })
                }
            })
        })
    }
    getAvatar(event){
        var files = event.target.files;
        this.setState({
            userAvatar:files
        })
        if(window.FileReader) {
            var file = files[0];
            var fr = new FileReader();
            fr.onloadend = (e)=> {
                console.log(this.refs.avatar);
                this.refs.avatar.style.backgroundImage  = "url("+e.target.result+")";
                this.refs.avatar.style.backgroundSize = "100%";
            };
            fr.readAsDataURL(file);  //也是利用将图片作为url读出
        }
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.goBack()} title="注册"/>
            <div className="register-input-box" ref="avatar">
                +
                <input type="file" className="register-input" onChange={(e)=>this.getAvatar(e)}/>
            </div>
            <div className="cm-mlr-15 cm-mt-10">
               <List>
                   <Input
                       placeholder="请输入昵称"
                       value={this.state.userName}
                       onChange={(val)=>this.change({userName:val})}
                   />
               </List>
                <List>
                    <Input
                        placeholder="请输入手机号码"
                        value={this.state.mobile}
                        onChange={(val)=>this.change({mobile:val})}
                    />
                </List>
                <List>
                    <Input
                        placeholder="请输入验证码"
                        value={this.state.verifyCode}
                        onChange={(val)=>this.change({verifyCode:val})}
                        renderRight = {()=>this.renderRight()}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入密码"
                        value={this.state.password}
                        onChange={(val)=>this.change({password:val})}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入确认密码"
                        value={this.state.confirmPassword}
                        onChange={(val)=>this.change({confirmPassword:val})}
                    />
                </List>
                    <div className="cm-btn-fill cm-mt-40" onClick={()=>this.register()}>注册</div>
                <div className="cm-tx-r login-operate" onClick={()=>this.goLogin()}>
                   已有账户？登录
                </div>
            </div>
        </div>
    }
}
