/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import ReactDOM from 'react-dom';
import search from '../../images/index/search.png';
import scan from '../../images/index/scan-in.png';
import banner from '../../images/index/banner.jpg';
import close from '../../images/index/close.png'
import {Carousel,ListView} from 'antd-mobile';
import axios from "axios";
import wx from 'react-jweixin';
import {getData,goNext,setState} from '../../utils';
import './index.css';
export default class Index extends React.Component {
    constructor(props){
        super(props)
        this.initData = [];
        const dataSource = new ListView.DataSource({
            rowHasChanged: (row1, row2) => row1 !== row2,
        })
        this.state = {
            isLoading: true,
            dataSource: dataSource.cloneWithRows(this.initData),
            height: document.documentElement.clientHeight-180,
            currentPage:0,
            pageSize:10,
            typeList:[],
            activeType:0,
            productList:[],
            bannerList:[],
            isShowClose:false
        }
    }
    changeType(index){
        var box = this.refs.box;
        //box.style.transform = "translateX("+self.slideWidth*index+"px)";
        var count = parseInt((this.state.typeList.length*5*10-window.innerWidth+50)/(5*10));
        if(index>=3&&index<=3+count){
            box.style.transform = "translateX("+(-(index-2)*5)+"rem)";
            box.style.transition = "transform .5s";
        };
        this.getProductListByType(index);
        setState(this,{activeType:index})
    }
    goNext(item){
        goNext(this,"indexDetail",item);
    }
    getProductLst(){
        var that = this;
        axios.post( "http://localhost:8080/getProductList",{pageNo: this.state.currentPage++,
            pageSize: this.state.pageSize,}).then(function (res) {
            console.log(res.data.result);
            that.initData = res.data.result.concat(that.initData);
            setTimeout(() => {
                that.setState({
                    dataSource: that.state.dataSource.cloneWithRows(that.initData),
                    isLoading: false,
                },function () {
                    console.log(this.state.dataSource)
                });
            }, 600);
        })
    }
    getBannerList(){
        var that = this;
        getData('post','index/bannerList',{},function (res) {
            console.log(res);
            that.setState({
                bannerList: res.data.result
            })
        })
    }
    getProductListByKey(){
        var value = this.refs.input.value;
        var that = this;
        getData('post','index/productListByKey',{param:value},function (res) {
            that.setState({
                productList: res.data.result
            })
        })
    }
    getProductListByType(index){
        var that = this;
        getData('post','index/productListByType',{type:index},function (res) {
            that.setState({
                productList: res.data.result
            })
        })
    }
    getProductType(){
        var that = this;
        getData('post','index/productTypeList',{},function (res) {
            that.setState({
                typeList: res.data.result
            })
        })
    }
    componentDidMount(){
        this.getProductLst();
        this.getProductType();
        this.getBannerList();
    }
    onEndReached = (event) => {
        if (this.state.isLoading ) {
            return false;
        }
        if ( !this.state.hasMore) {
            return false;
        }
        this.setState({ isLoading: true });
        setTimeout(() => {
            this.getProductLst();
        }, 1000);
    }
    onScroll(e){
        console.dir(document.body)
        console.log(e);
    }
    closeValue(){
        this.refs.input.value = '';
        this.setState({
            isShowClose:false
        })
        console.log(this.refs.input.value)
    }
    onChange(){
        this.setState({
            isShowClose:true
        })
    }
    scan(){
        wx.config({
            debug : false, // 开启调试模式,调用的所有api的返回值会在客户端alert出来，若要查看传入的参数，可以在pc端打开，参数信息会通过log打出，仅在pc端时才会打印。
            appId : d.appId, // 必填，公众号的唯一标识
            timestamp : d.timestamp, // 必填，生成签名的时间戳
            nonceStr : d.nonceStr, // 必填，生成签名的随机串
            signature : d.signature,// 必填，签名，见附录1
            jsApiList : [ 'scanQRCode' ]
            // 必填，需要使用的JS接口列表，所有JS接口列表见附录2
        });
        wx.scanQRCode({
            // 默认为0，扫描结果由微信处理，1则直接返回扫描结果
            needResult : 1,
            desc : 'scanQRCode desc',
            success : function(res) {
                var codes = res.resultStr;
                alert("扫码识别到的codes："+codes);
            }
        });
    }
    render() {
        console.log(this.state.dataSource);
        const row = (rowData, sectionID, rowID) => {
            console.log(rowData);
            return (
                    <div  className="index-product" key={rowID} onClick={()=>this.goNext()}>
                        <img src={rowData && rowData.productImg} alt="" className="index-img"/>
                        <div className="index-des">{rowData && rowData.productDes}</div>
                        <div className="index-price">￥{rowData && rowData.productPrice}</div>
                    </div>
            );
        };
        return (
            <div>
                <div className="index-top">
                    <div className="cm-flex cm-width-full">
                        <img src={search} alt="" className="index-search" onClick={()=>this.getProductListByKey()}/>
                        <input type="text"  ref="input" className="index-input" onChange={()=>this.onChange()} placeholder="请输入商品关键字"/>
                        {this.state.isShowClose?
                            <img src={close} alt="" className="index-close" onClick={()=>this.closeValue()}/>:null
                        }
                    </div>
                    <img src={scan} alt="" className="cm-img-small" onClick={()=>this.scan()}/>
                </div>
                <div className="cm-mlr-15">
                    <div className="index-carousel">
                        {this.state.bannerList? <Carousel>
                              {this.state.bannerList.map(item => (
                                  <img src={item.image} alt="" className="cm-img-banner index-banner"/>
                              ))}
                            </Carousel>:null
                        }
                    </div>
                <div className="cm-flex cm-mtb-10 cm-ai-c index-scroll" ref="fixed">
                    <div className="cm-flex cm-ai-c" ref="box">
                    {this.state.typeList && this.state.typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?"index-type-active":"index-type"} onClick={()=>this.changeType(index)}>{item.name}</div>
                        )
                    })}
                    </div>
                </div>
                    <ListView
                        useBodyScroll={true}
                        ref={el => this.lv = el}
                        dataSource={this.state.dataSource}
                        renderFooter={() => (<div style={{ paddingBottom: 60, textAlign: 'center' }}>
                            {this.state.isLoading ? 'Loading...' : 'Loaded'}
                        </div>)}
                        renderBodyComponent={() => <MyBody />}
                        renderRow={row}
                        pageSize={4}
                        onScroll={(e) => this.onScroll(e)}
                        scrollRenderAheadDistance={500}
                        onEndReached={this.onEndReached}
                        onEndReachedThreshold={10}
                    />
            </div>
            </div>
        )
    }
}
function MyBody(props) {
    return (
        <div>
        <div className="index-box">
            {props.children}
        </div>
        </div>
    );
}
