/**
 * Created by Lenovo on 2019/8/6.
 */
import Mock from 'mockjs'
import Config from './envConfig';
import banner from './images/index/banner.jpg'
Config.serverUrl = "http://mock/";
const url = {
    //首页模块接口
    bannerList: Config.serverUrl+'index/bannerList',
    productTypeList: Config.serverUrl+'index/productTypeList',
    productList: Config.serverUrl+'index/productList',
    productListByKey: Config.serverUrl+'index/productListByKey',
    productListByType: Config.serverUrl+'index/productListByType',

    //发布模块接口
    publishProduct: Config.serverUrl+'publish/publishProduct',

    //消息模块接口
    messageList: Config.serverUrl+'message/messageList',
    getMessageDetailById: Config.serverUrl+'message/getMessageDetailById',
    sendMessage: Config.serverUrl+'message/sendMessage',

    //登陆与注册模块接口
    register: Config.serverUrl+'register/register',
    getSmsCode: Config.serverUrl+'register/getSmsCode',
    login: Config.serverUrl+'login/login',

    //个人中心模块接口
    getProductByState: Config.serverUrl+'personal/getProductByState',
    updatePwd: Config.serverUrl+'personal/updatePwd',
    retrievePwd: Config.serverUrl+'personal/retrievePwd',
    updateCustomerInfo: Config.serverUrl+'personal/updateCustomerInfo',
    updateProductInfo: Config.serverUrl+'personal/updateProductInfo',
    logout: Config.serverUrl+'personal/logout',
}
Mock.mock(url.productList, {data: {
    'result|5': [{
        'id|+1': 1,
        'productImg': Mock.Random.image('180x200'),
        'productDes|1': ['九成新哦，先到先得','貂皮大衣','八成新','这个东西好好看的'],
        'productPrice|18-280': 20
    }]
}});
Mock.mock(url.productListByKey, {data: {
    'result|5': [{
        'id|+1': 1,
        'productImg': Mock.Random.image('180x200'),
        'productDes|1': ['九成新哦，先到先得','貂皮大衣','八成新','这个东西好好看的'],
        'productPrice|18-280': 20
    }]
}});
Mock.mock(url.productListByType, {data: {
    'result|5': [{
        'id|+1': 1,
        'productImg': Mock.Random.image('180x200'),
        'productDes|1': ['九成新哦，先到先得','貂皮大衣','八成新','这个东西好好看的'],
        'productPrice|18-280': 20
    }]
}});
Mock.mock(url.productTypeList, {data: {
    'result|10': [{
        'id|+1': 1,
        'name|1': ['数码','服装','居家','美妆','家电','玩具','运动'],
    }]
}})
Mock.mock(url.bannerList, {data: {
    'result|5': [{
        'id|+1': 1,
        'image|1': [banner,banner,banner],
    }]
}})
Mock.mock(url.publishProduct, {data: {
    'result|10': [{
        'id|+1': 1,
        'image': Mock.Random.image('180x200'),
    }]
}})

// export let praiseList = [{avatar:avatar}];
// export let commentList = [{avatar:avatar,comment:'穿过几次？',userName:'超人'}];


Mock.mock(url.register, {
    'code': 0,
    'msg': "注册成功",
})
Mock.mock(url.getSmsCode, {
    'smsCode': "123456",
})
Mock.mock(url.login, {
    'code': 0,
    'msg': "注册成功",
    'data':{
        token:"wxopoopddd",
        nickName:'杏子',
        userAvatar:"",
    }
})
