/**
 * Created by Lenovo on 2019/8/7.
 */
import React from 'react';
import right from '../../images/right.png';
import select from '../../images/select.png';
import '../common.css';
import './index.css';
import close from '../../images/close.png';
export default class Select extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isShow: false
        }
    }
    componentDidMount(){

    }
    showBox() {
        var that = this;
        this.setState({
            isShow: !this.state.isShow
        },function () {
            var height = this.refs && this.refs.box.offsetHeight;
            if(height>=300){
                console.log(that.refs.box);
                that.refs.box.style.height = "300px";
            }
        })
    }
    closeBox(){
        this.setState({
            isShow: false
        })
    }
    render() {
        const {options, babel, rightRender} =this.props;
        return (
            <div className="cm-flex cm-ai-c cm-jc-sb">
                <label htmlFor="">{babel}</label><img src={right} alt="" className="publish-img"
                                                      onClick={() => this.showBox()}/>
                {this.state.isShow ?
                    <div className="select-position">
                        <div className="select-box" ref="box" onClick={()=>this.showBox()}>
                        <div className="cm-ptb-10 cm-c-main cm-ai-c cm-flex cm-jc-sb cm-mlr-10">
                            <div className="cm-ai-c cm-flex">
                                <img className="cm-img-small-icon" src={select} alt="" onClick={()=>this.closeBox()}/>
                                <span className="cm-ml-10">请选择分类</span>
                            </div>
                            <img  className="cm-img-small-icon" src={close} alt="" onClick={()=>this.closeBox()}/></div>
                        {options.map((item) => {
                            return <div className="cm-ptb-10 cm-border-top">{item.label}</div>
                        })}
                        </div></div> : null}
            </div>
        )

    }
}
