/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import praise from '../../images/index/praise.png';
import alreadyPraise from '../../images/index/already-praise.png';
import comment from '../../images/index/comment.png';
import avatar from '../../images/index/avatar.jpg';
import banner from '../../images/index/banner.jpg';
import './index.css';
export default class IndexDetail extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            praiseList:[],
            commentList:[]
        }
        this.item = {
            avatar:avatar,
            userName:'杏子',
            proDes:'九成新的鞋子',
            proPrice:187,
            proImg:banner,
        }
    }
    render() {
        return (
            <div className="cm-mlr-15">
                <div className="detail-person">
                    <img src={avatar} alt="" className="cm-img-small"/>
                    <div>{this.item.userName}</div>
                </div>
                <div className="detail-des">
                   <div>{this.item.proPrice}</div>
                    <div>{this.item.proDes}</div>
                </div>
                <div className="detail-img">
                    <img src={this.item.proImg} alt=""/>
                </div>
                <div className="cm-flex cm-mtb-10 cm-ai-c">
                    <div>点赞<span>{this.state.praiseList.length}</span></div>
                    {this.state.praiseList.map((item,index)=>{
                        return(
                            <div className={this.state.activeType == index?"index-type-active":"index-type"} >{item.name}</div>
                        )
                    })}
                </div>
                <div >
                    {this.state.productList.map((item,index)=>{
                        return(
                            <div className="index-product">
                                <img src={item.productImg} alt="" className="index-proImg"/>
                                <div className="index-proDes">{item.productDes}</div>
                                <div className="index-proPrice">￥{item.productPrice}</div>
                            </div>
                        )
                    })}
                </div>
            </div>
        )
    }
}
