/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import '../share.css';
import './index.css';
export default class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value:''
        }
    }
    componentDidMount(){

    }
    change(val){
        const {onChange}=this.props;
        onChange(val);
        this.setState({
            value:val
        })
    }
    render() {
        const {label,placeholder} =this.props;
        return (
            <div className="cm-flex cm-ai-c">
                <label htmlFor="">{label}</label><input type="text" ref="input" className="cm-ml-10" placeholder={placeholder}/>
            </div>
        )
    }
}
