/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast,ImagePicker} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
const Item = List.Item;
export default class VerifyMobile extends React.Component {
    constructor(props) {
        super(props);
        let user = localStorage.getItem("userInfo");
        user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        console.log(user);
        this.state = {
            userAvatar:[user.userAvatar],
            getCode:'获取验证码',
            userName: user.userName,
            mobile: user.mobile,
            verifyCode:"",
            newVerifyCode:"",
            newMobile:"",
            isNext:false,
            title:"验证原手机"
        }
    }
    onLeftClick(){
        this.props.history.goBack();
    }
    change(state){
        this.setState(state)
    }
    getVerify(type){
        console.log(this.state.mobile);
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:type
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode == 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(type){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-04"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify(type)}> {this.state.getCode == "获取验证码"?"获取验证码":this.state.getCode+"秒重新获取"}</span></div>
        )
    }
    modify(){
        var arr = [
            {value:this.state.mobile,msg:"请输入手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:this.state.mobile,
                verifyCode:this.state.verifyCode
            };
            getData({
                method: 'post',
                url: 'verifyOldMobile',
                data: param,
                successCB: (res) => {
                    clearInterval(this.timer);
                    this.setState({
                        isNext:true,
                        title:"绑定新手机号",
                        getCode:"获取验证码"
                    })
                }
            })
        })
    }
    submit(){
        var arr = [
            {value:this.state.newMobile,msg:"请输入新手机号"},
            {value:this.state.newVerifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:this.state.newMobile,
                verifyCode:this.state.newVerifyCode
            };
            getData({
                method: 'post',
                url: 'bindNewMobile',
                data: param,
                successCB: (res) => {
                    Toast.success("绑定成功");
                   goNext(this,"personal")
                }
            })
        })
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.onLeftClick()} title={this.state.title}/>
            {this.state.isNext?
                <div>
                    <div className="cm-mlr-018 cm-mt-018">
                        <List>
                            <Input
                                label="新手机号"
                                maxLength={11}
                                placeholder="请填写手机号"
                                defaultValue={this.state.newMobile}
                                onChange={(val)=>this.change({newMobile:val})}
                                className="cm-c-999"
                            />
                        </List>
                        <List>
                            <Input
                                label="验证码"
                                placeholder="请输入短信验证码"
                                defaultValue={this.state.newVerifyCode}
                                onChange={(val)=>this.change({newVerifyCode:val})}
                                renderRight = {()=>this.renderRight(4)}
                            />
                        </List>
                        <Button className="cm-mt-040" onClick={() => this.submit()}>完成</Button>
                    </div>
                </div>:
                <div>
                    <div className="cm-mlr-018 cm-mt-018">
                        <List>
                            <Input
                                label="手机号"
                                maxLength={11}
                                placeholder="请输入手机号"
                                defaultValue={this.state.mobile}
                                onChange={(val)=>this.change({userName:val})}
                                className="cm-c-999"
                            />
                        </List>
                        <List>
                            <Input
                                label="验证码"
                                placeholder="请输入短信验证码"
                                defaultValue={this.state.verifyCode}
                                onChange={(val)=>this.change({verifyCode:val})}
                                renderRight = {()=>this.renderRight(3)}
                            />
                        </List>
                        <Button className="cm-mt-040" onClick={() => this.modify()}>下一步</Button>
                    </div>
                </div>
            }
        </div>
    }
}
