/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
import ModifyMobile from './modifyMobile';
const Item = List.Item;
export default class VerifyMobile extends React.Component {
    constructor(props) {
        super(props);
    }
    modify(opt){
        console.log(opt);
        var arr = [
            {value:opt.mobile,msg:"请输入手机号"},
            {value:opt.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:opt.mobile,
                verifyCode:opt.verifyCode
            };
            getData({
                method: 'post',
                url: 'verifyOldMobile',
                data: param,
                successCB: (res) => {
                    clearInterval(this.timer);
                    goNext(this,"bindNewMobile");
                    this.setState({
                        getCode:"获取验证码"
                    })
                }
            })
        })
    }
    render() {
        let user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        return(<ModifyMobile
                 title="验证原手机"
                 mobile = {user.mobile}
                 buttonText = "下一步"
                 submit = {(opt)=>this.modify(opt)}
                 type = {3}
            />)
    }
}
