/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import right from '../../images/personal/right.png';
import avatar from '../../images/personal/avatar.jpg';
import sale from '../../images/personal/sale.png';
import buy from '../../images/personal/buy.png';
import publish from "../../images/personal/publish.png";
import modify from '../../images/personal/modify.png';
import find from '../../images/personal/find.png';
import {Title, List,Button,TabBottom} from '../../share';
import {getData, goNext} from '../../utils';
const Item = List.Item;
export default class Personal extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            info:[
                {leftTitle:"我发布的",leftIcon:[publish],rightTitle:0,link:"publish"},
                {leftTitle:"我卖出的",leftIcon:[sale],rightTitle:0,link:"buy"},
                {leftTitle:"我买到的",leftIcon:[buy],rightTitle:0,link:"sale"},
                {leftTitle:"修改密码",leftIcon:[modify],rightTitle:0,link:"modifyPwd"}
            ]
        }
    }

    goNextPage() {
        goNext(this, 'login');
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'logout',
            data: {},
            successCB: (res) => {
                localStorage.removeItem("userInfo");
                localStorage.removeItem("token");
                goNext(this, 'login');
            }
        })
    }
    render() {
        var user = JSON.parse(localStorage.getItem("userInfo"))||{};
        console.log(user);
        return <div>
            <Title title="个人中心" isHome={true}/>
            <div className="cm-mlr-10">
                <div className="cm-mtb-20 cm-flex cm-ai-c" onClick={() => this.goNextPage()}>
                    <img src={user.userAvatar} alt="" className="cm-img-50 cm-border-radius-half"/>
                    <div className="cm-ml-10">
                        <div className="cm-fw-bold cm-fs-18">{user.userName}</div>
                        <div className="cm-c-999 cm-mt-10">当前卖出累计金额 <span className="cm-c-main cm-fs-18">￥12.78</span>
                        </div>
                    </div>
                </div>
                <div className="cm-line"></div>
                {this.state.info.map((item,index)=>{
                    return(
                    <List key={index} onClick={()=>goNext(this, item.link,item)}>
                        <Item
                            leftIcon={item.leftIcon}
                            leftTitle={item.leftTitle}
                            rightTitle={item.rightTitle}
                        />
                    </List>
                    )
                })}
                <Button className="cm-mt-40" onClick={()=>this.goLogin()}>安全退出</Button>
                <TabBottom history={this.props.history} activeNum={3}/>
            </div>
        </div>
    }
}
