/**
 * Created by Lenovo on 2019/9/4.
 */
/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './personal.css';
import {Title,List} from '../../share';
import {getData, goNext,getTimeFormat,getTimeHour} from '../../utils';
const Item = List.Item;
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.item = this.props.history.location.state ||{};
        console.log(this.item);
        this.count = 0;
        this.state = {
            chatList:[],
            index:0,
            value:null
        }
    }
    componentDidMount(){
        this.goLogin();
        this.getMyProductList();
        var textarea = document.getElementsByTagName("textarea");
        for(var i=0;i<textarea.length;i++){
            textarea[i].style.height = '3.5rem';
            textarea[i].scrollTop = 0; //防抖动
            textarea[i].style.height = textarea[i].scrollHeight + 'px';
            textarea[i].addEventListener('input',function (e) {
                e.target.style.height = 'auto';
                e.target.scrollTop = 0; //防抖动
                if(e.target.scrollHeight<35){
                    e.target.style.height = '35px';
                }else {
                    e.target.style.height = e.target.scrollHeight + 'px';
                }
            })
        }
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                console.log(res);
            }
        })
    }
    onLeftClick(){
        this.props.history.push({pathname:"messageDetail"});
    }
    changeState(e){
        console.log(e);
        this.setState({
            value:e.target.value
        })
    }
    getMyProductList(){
        getData({
            method: 'post',
            url: 'logout',
            data: {},
            successCB: (res) => {
                localStorage.removeItem("userInfo");
                localStorage.removeItem("token");
                goNext(this, 'login');
            }
        })
    }
    render() {
        return <div>
            <Title title="我发布的"/>
            <div className="cm-mlr-10">
                <div className="cm-mtb-20 cm-flex cm-ai-c" onClick={() => this.goNextPage()}>
                    <img src={user.userAvatar} alt="" className="cm-img-50 cm-border-radius-half"/>
                    <div className="cm-ml-10">
                        <div className="cm-fw-bold cm-fs-18">{user.userName}</div>
                        <div className="cm-c-999 cm-mt-10">我发布的宝贝供 <span className="cm-c-main cm-fs-18">2</span>件
                        </div>
                    </div>
                </div>
                <div className="cm-line"></div>
                {this.state.info.map((item,index)=>{
                    return(
                        <List key={index} onClick={()=>goNext(this, item.link,item)}>
                            <Item
                                leftIcon={item.leftIcon}
                                leftTitle={item.leftTitle}
                                rightTitle={item.rightTitle}
                            />
                        </List>
                    )
                })}
            </div>
        </div>
    }
}
