/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast,ImagePicker} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
const Item = List.Item;
export default class ForgetPwd extends React.Component {
    constructor(props) {
        super(props);
        let user = localStorage.getItem("userInfo");
        user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        console.log(user);
        this.state = {
            password:'',
            newPassword:"",
            confirmPwd:'',
        }
    }
    onLeftClick(){
        this.props.history.goBack();
    }
    change(state){
        this.setState(state)
    }
    submit(){
        var arr = [
            {value:this.state.password,msg:"请输入旧密码"},
            {value:this.state.newPassword,msg:"请输入新密码"},
            {value:this.state.confirmPwd,msg:"请输入确认密码"},
            {value:this.state.newPassword == this.state.confirmPwd,msg:"密码与确认密码不一致"},
        ]
        checkParam(arr,()=> {
            getData({
                method: 'post',
                url: 'updatePwd',
                data:{
                    "oldPwd":this.state.password,
                    "newPwd":this.state.newPassword,
                    "confirmPwd":this.state.confirmPwd
                },
                successCB: (res) => {
                    console.log(res);
                    if(res.code == 0){
                        Toast.success(res.message);
                        goNext(this,"personal")
                    }else {
                        Toast.fail(res.message);
                    }
                }
            })
        })
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.onLeftClick()} title="忘记密码"/>
            <div className="cm-mlr-018 cm-mt-018">
                <List>
                    <Input
                        type="password"
                        placeholder="请输入旧密码"
                        onChange={(val)=>this.change({password:val})}
                        maxLength={16}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入新密码"
                        onChange={(val)=>this.change({newPassword:val})}
                        maxLength={16}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入确认密码"
                        value={this.state.confirmPwd}
                        onChange={(val)=>this.change({confirmPwd:val})}
                        maxLength={16}
                    />
                </List>
                <Button type="fill" className="cm-mt-080" onClick={() => this.submit()}>确认修改</Button>
            </div>
        </div>
    }
}
