import React from 'react';
import {ImagePicker,WhiteSpace} from 'antd-mobile';
import './publish.css';
import add from '../../images/add.png';
import Title from '../../components/Title';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            files:[]
        }
    }
    onChange = (files, type, index) => {
        console.log(files, type, index);
        this.setState({
            files,
        });
    };
    onAddImageClick = (e) => {
        e.preventDefault();
        console.log(e);
        // this.setState({
        //     files: this.state.files.concat({
        //         url: 'https://zos.alipayobjects.com/rmsportal/hqQWgTXdrlmVVYi.jpeg',
        //         id: '3',
        //     }),
        // });
    };
    onTabChange = (key) => {
        console.log(key);
    };
    render(){
        const { files } = this.state;
        return(
            <div>
                <Title isHome={true} title="商品发布"/>
                <div className="cm-mlr-15 cm-mt-10">
                    <textarea name="" id="" cols="30" className="cm-width-full publish-textarea" rows="10" placeholder="描述商品转手原因，入手渠道和使用感受"></textarea>
                    <ImagePicker
                        files={files}
                        onChange={this.onChange}
                        onImageClick={(index, fs) => console.log(index, fs)}
                        selectable={files.length < 5}

                    />
                </div>
                <div className="cm-line"></div>
                <div className="cm-mlr-15 cm-mt-20">
                    <div className="cm-fw-bold cm-c-333 cm-tx-c cm-fs-20">价格与分类</div>
                    <div className="publish-label-box"><label htmlFor="">价格</label><input type="text" className="publish-input"/></div>
                    <div className="publish-label-box"><label htmlFor="">分类</label><input type="text" className="publish-input"/></div>
                </div>
                <div className="cm-btn cm-mlr-15 publish-mar-40">确认发布</div>
            </div>
        )
    }
}
