/**
 * Created by Lenovo on 2019/9/15.
 */
/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import {List,Input,Button} from '../../share';
import {getData,isDefine} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/account/verify-code.png';
export default class ModifyMobile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getCode:'获取验证码',
            mobile: props.mobile?props.mobile:"",//验证原手机的时候手机号直接从父组件传递过来
            verifyCode:"",//初始化验证码
        };
        this.type = props.type;
    }
    change(state){
        this.setState(state)
    }
    //验证手机号
    getVerify(){
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:this.type
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode === 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-04"/><span className="cm-c-main" onClick={()=>this.getVerify()}> {this.state.getCode === "获取验证码"?"获取验证码":this.state.getCode+"秒重新获取"}</span></div>
        )
    }
    //点击下一步或者完成的回调
    submit(){
        if(this.props.submit){
            this.props.submit({mobile:this.state.mobile,verifyCode:this.state.verifyCode});
        }
    }
    render() {
        return <div>
                <div className="cm-mlr-018 cm-mt-018">
                    <List>
                        <Input
                            label="手机号"
                            maxLength={11}
                            placeholder="请输入手机号"
                            defaultValue={this.state.mobile}
                            onChange={(val)=>this.change({mobile:val})}
                            className="cm-c-999"
                        />
                    </List>
                    <List>
                        <Input
                            label="验证码"
                            placeholder="请输入短信验证码"
                            maxLength={6}
                            defaultValue={this.state.verifyCode}
                            onChange={(val)=>this.change({verifyCode:val})}
                            renderRight = {this.renderRight()}
                        />
                    </List>
                    <Button type="fill" className="cm-mt-080" onClick={() => this.submit()}>{this.props.buttonText}</Button>
                </div>
            </div>
    }
}
