/**
 * Created by Lenovo on 2019/8/18.
 */
//import React from 'react';
import * as React from 'react';
import classnames from 'classnames';
import './index.css';
import {defaultProps} from './defaultProps';
export interface ButtonProps{
    prefixCls?: string;
    onClick?: React.MouseEventHandler<HTMLElement>;
    type?: 'primary' | 'fill';
    children?: React.ReactNode;
    className?: string;
}


export default class Button extends React.Component<ButtonProps, any> {
    static defaultProps = defaultProps;
    constructor(props:any) {
        super(props);
    }
    handleClick: React.MouseEventHandler<HTMLElement> = e => {
        const { onClick } = this.props;
        if (onClick) {
            (onClick as React.MouseEventHandler<HTMLElement>)(e);
        }
    };
    render() {
        const {prefixCls,type,children,className} =this.props;
        const wrapCls = classnames(prefixCls, className, {
            [`${prefixCls}-btn`]: (type === 'primary'||!type),
            [`${prefixCls}-btn-fill`]: type === 'fill',
        });
        return(<div className={wrapCls} onClick={this.handleClick}>{children}</div>)
    }
}
