import React from 'react';
import {ImagePicker,Toast} from 'antd-mobile';
import './publish.css';
import {Select,Title,List,Input} from '../../share';
import {getData,goNext,checkParam,isDefine} from '../../utils';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            productName:'',
            productImages:[],
            typeList:[],
            productType:'',
            productPrice:0,
            productDes:'',
        };
    }
    componentWillMount(){
        var obj = localStorage.getItem("userInfo");
        if(isDefine(obj)){

        }
    }
    componentDidMount(){
        this.getTypeList();
    }
    changeType(item){
        this.setState({
            productType : item.id
        })
    }
    onChange = (productImages, type, index) => {
        console.log(productImages, type, index);
        this.setState({
            productImages,
        });
    }
    changeDes(event){
        this.setState({
            productDes:event.target.value
        })
    }
    //金额正则校验
    changePrice(event){
        event.target.value = event.target.value.replace(/[^\d.]/g,"");
        //清除“数字”和“.”以外的任意字符
        event.target.value = event.target.value.replace(/\.{2,}/g,".");
        //只保留第一个“. ”，清除多余连续的“.”
        event.target.value = event.target.value.replace(".","$#$").replace(/\./g,"").replace("$#$",".");
        //将第一个“.”用特殊符号替换，其他“.”都转换为空，再将特殊符号用“.”替换回来,目的是为了保证清除掉非连续“.”
        event.target.value = event.target.value.replace(/^(\-)*(\d+)\.(\d\d).*$/,'$1$2.$3');
        //只能输入两个小数
        if(event.target.value.indexOf(".")< 0 && event.target.value !=""){
        //以上已经过滤，此处控制的是如果没有小数点，首位不能为类似于 01、02的金额
            event.target.value= parseFloat(event.target.value);
        }
        //通过 setState 改变金额
        this.setState({
            productPrice:event.target.value
        })
    }

    getTypeList(){
        if(isDefine(localStorage.getItem("typeList"))){
            this.setState({
                typeList:JSON.parse(localStorage.getItem("typeList"))
            })
        }
    }
    submit(){
        console.log("类型是"+this.state.productType)
        var arr = [
            {value: this.state.productDes, msg: "请输入商品描述"},
            {value: this.state.productImages[0], msg: "请添加图片"},
            {value: this.state.productName, msg: "请输入商品名称"},
            {value: this.state.productPrice, msg: "请输入价格"},
            {value: this.state.productType, msg: "请选择分类"},
        ];
        checkParam(arr,()=>{
            let param = new FormData(); //创建form对象
            for (var i = 0; i < this.state.productImages.length; i++){
                param.append('productImages',this.state.productImages[i].file);//通过append向form对象添加数据
            }
            param.append('productDesc',this.state.productDes);
            param.append('productName',this.state.productName);
            param.append('productPrice',this.state.productPrice);
            param.append('productTypeId',this.state.productType);
                getData({
                    method: 'post',
                    url: 'publishProduct',
                    headers:{
                        'Content-Type':'multipart/form-data',
                        'deviceId':'11',
                        'requestId':"66",
                        'clientVersion':"66",
                        'timestamp':'66',
                        'token':"42393dde23cb86c20190813223416"
                    },
                    data: param,
                    successCB: (res) => {
                        goNext(this,"app",0);
                    }
                })
        })
    }
    change(state){
        this.setState(state)
    }
    render(){
        const { productImages } = this.state;
        return(
            <div>
                <Title isHome={true} title="商品发布"/>
                <div className="cm-mlr-15 cm-mt-10">
                    <textarea name="" id="" cols="30"
                              onChange={(e)=>this.changeDes(e)}
                              className="cm-width-full publish-textarea" rows="10" placeholder="描述商品转手原因，入手渠道和使用感受"></textarea>
                    <ImagePicker
                        files={productImages}
                        onChange={this.onChange}
                        onImageClick={(index, fs) => console.log(index, fs)}
                        selectable={productImages.length < 9}
                    />
                </div>
                <div className="cm-line"></div>
                <div className="cm-mlr-15">
                    <List>
                        <Input
                            label="名称"
                            inputStyle = {{textAlign:"right"}}
                            placeholder="请输入商品名称"
                            value={this.state.productName}
                            onChange={(val)=>this.change({productName:val})}
                        />
                    </List>
                <List>
                    <Input
                        label="价格"
                        inputStyle = {{textAlign:"right"}}
                        placeholder="请输入商品价格"
                        value={this.state.productPrice}
                        onChange={(val)=>this.change({productPrice:val})}
                        maxLength={11}
                    />
                </List>
                <List>
                    <Select
                         options={this.state.typeList}
                         babel="分类"
                         onChange={this.changeType.bind(this)}
                    />
                </List>
                </div>
                <div className="cm-tx-c cm-mt-40">
                <span className="cm-btn-small" onClick={()=>this.submit()}>确认发布</span>
                </div>
            </div>
        )
    }
}
