import React from 'react';
import {ImagePicker,WhiteSpace,Picker,List,InputItem} from 'antd-mobile';
import './publish.css';
import add from '../../images/add.png';
import right from '../../images/right.png';
import {Select,Title} from '../../share';
import {getData,goNext,setState} from '../../utils';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            files:[],
            typeList:[{label:"服饰"},{label:"运动"},{label:"饮料"},{label:"苹果"},{label:"梨子"}],
            type:'',
            money:0,
            text:'',
        }
    }
    componentDidMount(){
        this.getTypeList();
    }
    changeType(val){
        this.setState({
            type : val
        })
    }
    onChange = (files, type, index) => {
        console.log(files, type, index);
        this.setState({
            files,
        });
    }
    changeDes(event){
        this.setState({
            text:event.target.value
        })
    }
    //金额正则校验
    changePrice(event){
        event.target.value = event.target.value.replace(/[^\d.]/g,"");
        //清除“数字”和“.”以外的任意字符
        event.target.value = event.target.value.replace(/\.{2,}/g,".");
        //只保留第一个“. ”，清除多余连续的“.”
        event.target.value = event.target.value.replace(".","$#$").replace(/\./g,"").replace("$#$",".");
        //将第一个“.”用特殊符号替换，其他“.”都转换为空，再将特殊符号用“.”替换回来,目的是为了保证清除掉非连续“.”
        event.target.value = event.target.value.replace(/^(\-)*(\d+)\.(\d\d).*$/,'$1$2.$3');
        //只能输入两个小数
        if(event.target.value.indexOf(".")< 0 && event.target.value !=""){
        //以上已经过滤，此处控制的是如果没有小数点，首位不能为类似于 01、02的金额
            event.target.value= parseFloat(event.target.value);
        }
        //通过 setState 改变金额
        this.setState({
            money:event.target.value
        })
    }

    getTypeList(){
        var that = this;
        getData('post','index/bannerList',{
            productDes:this.state.text,
            productImg:this.state.files,
            productPrice:this.state.money,
            productType:this.state.type
        },function (res) {
            console.log(res);
            if(res){
                setState({
                    typeList:res.data.result
                })
            }
        })
    }
    submit(){
        var that = this;
        getData('post','index/bannerList',{
            productDes:this.state.text,
            productImg:this.state.files,
            productPrice:this.state.money,
            productType:this.state.type
        },function (res) {
            console.log(res);
            if(res){
                goNext(that,"index")
            }
        })
    }
    render(){
        const { files } = this.state;
        return(
            <div>
                <Title isHome={true} title="商品发布"/>
                <div className="cm-mlr-15 cm-mt-10">
                    <textarea name="" id="" cols="30"
                              onChange={(e)=>this.changeDes(e)}
                              className="cm-width-full publish-textarea" rows="10" placeholder="描述商品转手原因，入手渠道和使用感受"></textarea>
                    <ImagePicker
                        files={files}
                        onChange={this.onChange}
                        onImageClick={(index, fs) => console.log(index, fs)}
                        selectable={files.length < 9}
                    />
                </div>
                <div className="cm-line"></div>
                <div className="cm-mt-20 cm-mlr-15">
                    <div className="publish-label-box cm-flex">
                        <div className="publish-label">价格</div>
                        <input type="text" className="publish-input" maxLength="11" onChange={(e)=>this.changePrice(e)}/>
                    </div>
                    <div className="publish-label-box">
                        <Select
                            options={this.state.typeList}
                            babel="分类"
                            onChange={(val)=>this.changeType(val)}
                        />
                    </div>
                </div>
                <div className="cm-btn publish-mar-40" onClick={()=>this.submit()}>确认发布</div>
            </div>
        )
    }
}
