/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import search from '../../images/index/search.png';
import scan from '../../images/index/scan-in.png';
import banner from '../../images/index/banner.jpg';
import {typeList,productList} from '../../model/index/index';
import './index.css';
export default class Index extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            typeList:typeList,
            activeType:0,
            productList:productList
        }
    }
    changeType(index){
        this.setState({
            activeType:index
        })

    }
    goNext(item){
        this.props.history.push("indexDetail");
    }
    render() {
        return (
            <div className="cm-mlr-15">
                <div className="index-top">
                    <input type="text" className="index-input" placeholder="请输入商品关键字"/>
                    <img src={scan} alt="" className="cm-img-small"/>
                </div>
                <div>
                    <img src={banner} alt="" className="cm-img-banner"/>
                </div>
                <div className="cm-flex cm-mtb-10 cm-ai-c">
                    {this.state.typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?"index-type-active":"index-type"} onClick={()=>this.changeType(index)}>{item.name}</div>
                        )
                    })}
                </div>
                <div className="index-box">
                    {this.state.productList.map((item,index)=>{
                        return(
                            <div className="index-product" key={index} onClick={()=>this.goNext(item)}>
                                <img src={item.productImg} alt="" className="index-proImg"/>
                                <div className="index-proDes">{item.productDes}</div>
                                <div className="index-proPrice">￥{item.productPrice}</div>
                            </div>
                        )
                    })}
                </div>
            </div>
        )
    }
}
