/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './message.css';
import avatar from '../../images/personal/avatar.jpg';
import sale from '../../images/personal/sale.png';
import buy from '../../images/personal/buy.png';
import modify from '../../images/personal/modify.png';
import {Title} from '../../share';
import {getData, goNext} from '../../utils';
export default class MessageDetail extends React.Component {
    constructor(props) {
        super(props);
        this.item = this.props.history.location.state ||{};
        console.log(this.item);
    }
    componentDidMount(){
        this.goLogin();
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                console.log(res);
            }
        })
    }
    onLeftClick(){
        this.props.history.push({pathname:"messageDetail"});
    }
    sendMessage(){
            var userId = this.commentItem.fromUserId;
            if(this.commentItem.fromUserId == this.state.item.publishUserId){
                userId = this.commentItem.toUserId;
            }
            this.setState({
                isShowComment:!this.state.isShowComment
            });
            var value = this.refs.textarea.value;
            getData({
                method: 'get',
                url: '/socket/'+'{'+userId+'}',
                successCB: (res) => {
                    getData({
                        method: 'post',
                        url: 'commentOrReply',
                        data: {
                            productId:this.id,
                            content:value,
                            toUserId:userId,
                            replyId: this.commentItem.id,
                        },
                        successCB: (res) => {
                            this.getCommentList()
                        }
                    })
                }
            });
    }
    render() {
        return <div>
            <Title title={this.item.anotherUserName} onLeftClick={this.onLeftClick.bind(this)}/>
               <div className="cm-flex cm-ai-c cm-pad-10 cm-bc-white">
                   <img src={this.item.anotherUserAvatar} alt="" className="message-img-60 cm-mr-10"/>
                   <div>
                       <div className="cm-c-main cm-fs-15">￥123</div>
                       <div className="cm-c-999 cm-fs-12 cm-mt-10">交易前聊一聊</div>
                   </div>
               </div>
            <div className="cm-mar-10 cm-flex-column ">
               <div className="cm-tx-c">09:12</div>
            <div className="cm-flex cm-ai-c cm-tx-c cm-as-fe cm-mt-10">
                <div className="cm-mr-10 cm-bc-ddd detail-chat-height">商品还在吗</div>
                <img src={this.item.anotherUserAvatar} alt="" className="cm-img-large"/>
            </div>
            </div>
               <div></div>
               <div className="detail-position">
                <textarea ref="textarea" className="detail-textarea" name="" id="" cols="30" rows="2" placeholder="想跟他说点什么呢？"></textarea>
                <div className="detail-send" onClick={()=>this.sendMessage()}>发送</div>
            </div>

        </div>
    }
}
