import React from 'react';
import {ImagePicker,WhiteSpace,Picker,List,InputItem} from 'antd-mobile';
import './publish.css';
import add from '../../images/add.png';
import right from '../../images/right.png';
import {Select,Title} from '../../share';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            files:[],
            value:'',
        }
    }
    change(val){
        this.setState({
            value : val
        })
    }
    onChange = (files, type, index) => {
        console.log(files, type, index);
        this.setState({
            files,
        });
    }
    changePrice(event){
        console.log(event);
        var aa=event.persist();
        console.log(aa);
        // if (v && !/^(([1-9]\d*)|0)(\.\d{0,2}?)?$/.test(v)) {
        //     if (v === '.') {
        //         return '0.';
        //     }
        //     return prev;
        // }
        // return v;
    }
    render(){
        const { files } = this.state;
        const options = [{label:"服饰"},{label:"运动"},{label:"饮料"},{label:"苹果"},{label:"梨子"},{label:"香蕉"},{label:"香蕉1"},{label:"香蕉11"},{label:"香蕉111"}];
        return(
            <div>
                <Title isHome={true} title="商品发布"/>
                <div className="cm-mlr-15 cm-mt-10">
                    <textarea name="" id="" cols="30" className="cm-width-full publish-textarea" rows="10" placeholder="描述商品转手原因，入手渠道和使用感受"></textarea>
                    <ImagePicker
                        files={files}
                        onChange={this.onChange}
                        onImageClick={(index, fs) => console.log(index, fs)}
                        selectable={files.length < 9}
                    />
                </div>
                <div className="cm-line"></div>
                <div className="cm-mt-20 cm-mlr-15">
                    <div className="publish-label-box cm-flex">
                        <div className="publish-label">价格</div>
                        <input type="text" className="publish-input" maxLength="8" onChange={(e)=>this.changePrice(e)}/>
                    </div>
                    <div className="publish-label-box">
                        <Select
                            options={options}
                            babel="分类"
                        />
                    </div>
                </div>
                <div className="cm-btn publish-mar-40">确认发布</div>
            </div>
        )
    }
}
