/**
 * Created by Lenovo on 2019/10/14.
 */
/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import {Title} from '../../share';
import {getData, goNext,getTime} from '../../utils';
export default class AddressManage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            messageList:[]//初始化聊天列表
        }
    }
    componentDidMount(){
        this.getChatList();
    }
    //进入聊天详情页
    goDetail(item) {
        goNext(this, 'messageDetail',item);
    }
    //获取聊天列表内容
    getChatList(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                this.setState({
                    messageList:[]
                })
            }
        })
    }
    render() {
        return <div>
            <Title title="我的收货地址" that={this}/>
            <div className="cm-mlr-018">
                {this.state.messageList.length>0?this.state.messageList.map((item,index)=>{
                        var productImgs = item.productImgs && item.productImgs.split(",")||[];
                        var lastChatContent = item.lastChatContent && item.lastChatContent.slice(0,35);
                        return(
                            <div className="cm-flex cm-ai-c" key={index} onClick={()=>this.goDetail(item)}>
                                <img src={item.anotherUserAvatar} alt="" className="cm-img-08 cm-mr-018 cm-mt-018 cm-as-fs cm-border-radius-10"/>
                                <div className="cm-flex cm-ai-c cm-jc-sb cm-flex-1 cm-border-bottom-ddd cm-ptb-018">
                                    <div className="cm-flex-1">
                                        <div className="cm-c-333 cm-fw-bold cm-fs-028">{item.anotherUserName}</div>
                                        <div className="cm-c-666 cm-fs-026">{lastChatContent}</div>
                                        <div className="cm-c-999 cm-mt-009 cm-fs-024">{getTime(item.updateTime)}</div>
                                    </div>
                                    {productImgs[0]?
                                        <img src={productImgs[0]} alt="" className="cm-img-12"/>:
                                        <div className="cm-img-12 cm-c-999 cm-fs-026 cm-border-ddd cm-flex cm-ai-c cm-tx-c">该商品已下架</div>
                                    }
                                </div>
                            </div>
                        )
                    }):<div className="cm-tx-c cm-mtb-018 cm-border-bottom-ddd cm-c-666">添加地址</div>}
            </div>
        </div>
    }
}
