/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
import ModifyMobile from './modifyMobile';
const Item = List.Item;
export default class BindNewMobile extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            getCode:'获取验证码',
            mobile: '',
            verifyCode:"",
        }
    }
    onLeftClick(){
        this.props.history.goBack();
    }
    change(state){
        this.setState(state)
    }
    getVerify(){
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入新手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:4
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode == 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-04"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify()}> {this.state.getCode == "获取验证码"?"获取验证码":this.state.getCode+"秒重新获取"}</span></div>
        )
    }
    modify(){
        var arr = [
            {value:this.state.mobile,msg:"请输入手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:this.state.mobile,
                verifyCode:this.state.verifyCode
            };
            getData({
                method: 'post',
                url: 'verifyOldMobile',
                data: param,
                successCB: (res) => {
                    clearInterval(this.timer);
                    goNext(this,"bindNewMobile");
                    this.setState({
                        getCode:"获取验证码"
                    })
                }
            })
        })
    }
    submit(){
        var arr = [
            {value:this.state.mobile,msg:"请输入新手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:this.state.mobile,
                verifyCode:this.state.verifyCode
            };
            getData({
                method: 'post',
                url: 'bindNewMobile',
                data: param,
                successCB: (res) => {
                    Toast.success("绑定成功");
                    clearInterval(this.timer);
                    var user = JSON.parse(localStorage.getItem("userInfo"));
                    user.mobile = this.state.newMobile;
                    localStorage.setItem("userInfo",JSON.stringify(user));
                    goNext(this,"personal")
                }
            })
        })
    }
    render() {
        return(
            <div>
            <ModifyMobile
                title="绑定新手机"
                mobile = ""
                buttonText = "完成"
                submit = {(opt)=>this.submit(opt)}
                type = {4}
            />
        </div>)
    }
}
