/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import praise from '../../images/index/praise.png';
import alreadyPraise from '../../images/index/already-praise.png';
import comment from '../../images/index/comment.png';
import avatar from '../../images/index/avatar.jpg';
import banner from '../../images/index/banner.jpg';
import './index.css';
export default class IndexDetail extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            praiseList:[{avatar:avatar}],
            commentList:[{avatar:avatar,comment:'穿过几次？',userName:'超人'}]
        }
        this.item = {
            avatar:avatar,
            userName:'杏子',
            proDes:'九成新的鞋子',
            proPrice:187,
            proImg:banner,
        }
    }
    render() {
        return (
            <div className="cm-mlr-15">
                <div className="cm-flex cm-ai-c cm-border-bottom-gary cm-ptb-10 cm-fs-15">
                    <img src={avatar} alt="" className="cm-img-small cm-mr-10"/>
                    <div>{this.item.userName}</div>
                </div>
                <div className="cm-ptb-10 cm-fs-15">
                   <div className="cm-c-main">￥{this.item.proPrice}</div>
                    <div className="cm-c-999">{this.item.proDes}</div>
                </div>
                <div>
                    <img src={this.item.proImg} alt="" className="cm-img-banner"/>
                </div>
                <div className="cm-mtb-10">
                    <div className="detail-line"><span className="cm-mr-05">点赞</span><span>{this.state.praiseList.length}</span></div>
                    <div className="cm-flex cm-ai-c cm-mtb-10">
                        <img src={alreadyPraise} alt="" className="cm-img-small-icon cm-mr-10"/>
                    {this.state.praiseList.map((item,index)=>{
                        return(
                            <div className="cm-flex cm-ai-c">
                                <span>{this.state.praiseList.length}</span>
                                <span className="cm-mr-05">人点赞</span>
                                <img src={item.avatar} className="cm-img-small-icon"/>
                            </div>
                        )
                    })}
                    </div>
                </div>
                <div className="cm-mtb-10">
                    <div className="detail-line"><span className="cm-mr-05">留言</span><span>{this.state.commentList.length}</span></div>
                    {this.state.commentList.map((item,index)=>{
                        return(
                            <div className="cm-mtb-10">
                                <div className="cm-flex cm-mtb-10">
                                    <img src={item.avatar} alt="" className="cm-img-small-icon cm-mr-10"/>
                                    <div className="index-proDes">{item.userName}</div>
                                </div>
                                <div>{item.comment}</div>
                            </div>
                        )
                    })}
                </div>
                <div className="cm-flex cm-ai-c cm-jc-sb detail-bottom">
                    <div className="cm-flex cm-ai-c cm-jc-sb">
                    <div className="cm-flex cm-ai-c cm-mr-10"><span className="cm-mr-05">点赞</span><img src={praise} alt="" className="cm-img-small-icon"/></div>
                    <div className="cm-flex cm-ai-c"><span className="cm-mr-05">留言</span><img src={comment} alt="" className="cm-img-small-icon"/>
                    </div>
                    </div>
                    <div className="detail-btn">聊一聊</div>
                </div>
            </div>
        )
    }
}
