/**
 * Created by Lenovo on 2019/8/7.
 */
import React from 'react';
import right from '../image/select/right.png';
import select from '../image/select/select.png';
import '../share.css';
import './index.css';
import close from '../image/select/close.png';
export default class Select extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isShow: false,
            value:'',
            id:'',
            bottomHeight:350,
        }
    }
    componentDidMount(){

    }
    componentWillReceiveProps(props){
        this.state.value = props.options[0].name;//默认显示第一个类别
        this.state.id = props.options[0].id;//默认显示第一个类别
    }
    showBox(str) {
        this.setState({
            isShow: !this.state.isShow
        }, ()=>{
            if(str == "setHeight"){
                this.setHeight(this)
            }
        })
    }
    setHeight(that){
        var height = this.refs && this.refs.box.offsetHeight;
        if(height>=this.state.bottomHeight){
            console.log(that.refs.box);
            that.refs.box.style.height = this.state.bottomHeight+"px";
            that.refs.box.style.overflow = "scroll";
        }
    }
    closeBox(){
        this.setState({
            isShow: false
        })
    }
    change(val,id){
        const {onChange}=this.props;
        onChange(val,id);
        this.setState({
            value:val
        })
    }
    render() {
        const {options, babel} =this.props;
        return (
            <div className="cm-flex cm-ai-c cm-jc-sb cm-width-full">
                <div>{babel}</div>
                <div className="cm-flex cm-ai-c" onClick={() => this.showBox("setHeight")}>
                    <div>{this.state.value}</div>
                    <img src={right} alt="" className="publish-img"/>
                </div>
                {this.state.isShow ?
                    <div className="select-position">
                        <div className="select-box" ref="box" onClick={()=>this.showBox()}>
                        <div className="cm-c-main cm-ai-c cm-flex cm-jc-sb cm-mlr-10 select-top select-item">
                            <div className="cm-ai-c cm-flex">
                                <img className="cm-img-small-icon" src={select} alt="" onClick={()=>this.closeBox()}/>
                                <span className="cm-ml-10 cm-c-main">请选择分类</span>
                            </div>
                            <img  className="cm-img-small-icon" src={close} alt="" onClick={()=>this.closeBox()}/></div>
                            <div className="select-solid"></div>
                        {options.map((item,index) => {
                            console.log(item);
                            return <div key={index} className="select-item cm-border-top" onClick={()=>this.change(item.name,item.id)}>{item.name}</div>
                        })}
                        </div></div> : null}
            </div>
        )

    }
}
