import React from 'react';
import myAct from '../../images/app/my.png';
import my from '../../images/app/my-gray.png';
import msgAct from '../../images/app/message.png';
import msg from '../../images/app/message-gray.png';
import index from '../../images/app/index-gray.png';
import indexAct from '../../images/app/index.png';
import publish from '../../images/app/pubilsh-gray.png';
import publishAct from '../../images/app/publish.png';
import {isDefine} from '../../utils';
import {defaultProps} from './defaultProps';
import './index.css';
export default class TabBottom extends React.Component {
    static defaultProps = defaultProps;
    constructor(props) {
        super(props);
        console.log(this.props)
        this.state = {
            tabList:[
                {label:"首页",icon:index,iconAct:indexAct,path:"index"},
                {label:"发布",icon:publish,iconAct:publishAct,path:"publish"},
                {label:"消息",icon:msg,iconAct:msgAct,path:"message"},
                {label:"我的",icon:my,iconAct:myAct,path:"personal"},
            ],
            activeKey:0
        }
    }
    componentWillReceiveProps(props){
        console.log(props);
        this.setState({
            activeKey:props.activeKey||0
        })
    }
    changeTab(index,path){
        console.log(this);
        console.log(index);
        if(index == this.state.tabList.length - 1){
            let user = localStorage.getItem("userInfo");
            if(!isDefine(user)){
                this.props.history.push("login");
                return;
            }
        };
        this.props.history.push(path);
        this.setState({
            activeKey:this.props.activeNum
        })
    }
    render() {
        console.log(this.state.activeKey);
        this.state.activeKey = 1;
        const {prefixCls} = this.props;
        return (
            <div>
                <div className={prefixCls}>
                    {this.state.tabList.map((item,index)=>{
                        return(<div key={index} className={index == this.state.activeKey?prefixCls+"-tab-active":prefixCls+"-tab"} onClick={()=>this.changeTab(index,item.path)}>
                            <img className={prefixCls+"-icon"} src={index == this.state.activeKey?item.iconAct:item.icon} alt=""/>
                            <span className={prefixCls+"-span"}>{item.label}</span>
                        </div>)
                    })}
                </div>
            </div>
        )
    }
}
