/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './register.css';
import {Title,List,Input} from '../../share';
import {getData, goNext} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
const Item = List.Item;
export default class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            phone: '',
            verify:"",
            password:'',
            confirmPassword:''
        }
    }
    goBack(){
        goNext(this,'login')
    }
    change(state){
        this.setState(state)
    }
    goLogin(){
        goNext(this,'login')
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-small"/><span className="cm-c-main register-verify">获取验证码</span></div>
        )
    }
    register(){
        getData('post','register/register',{userName:"",mobile:"",smsCode:"",password:""}, (res)=> {
            if(res.data && res.data.code == 0){
                Toast.success(res.data,msg);
            }
        })
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.goBack()} title="注册"/>
            <div className="cm-mlr-15 cm-mt-10">
               <List>
                   <Input
                       placeholder="请输入昵称"
                       value={this.state.name}
                       onChange={(val)=>this.change({name:val})}
                   />
               </List>
                <List>
                    <Input
                        placeholder="请输入手机号码"
                        value={this.state.phone}
                        onChange={(val)=>this.change({phone:val})}
                    />
                </List>
                <List>
                    <Input
                        placeholder="请输入验证码"
                        value={this.state.verify}
                        onChange={(val)=>this.change({verify:val})}
                        renderRight = {()=>this.renderRight()}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入密码"
                        value={this.state.password}
                        onChange={(val)=>this.change({password:val})}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入确认密码"
                        value={this.state.confirmPassword}
                        onChange={(val)=>this.change({confirmPassword:val})}
                    />
                </List>
                    <div className="cm-btn-fill cm-mt-40" onClick={()=>this.register()}>注册</div>
                <div className="cm-tx-r login-operate" onClick={()=>this.goLogin()}>
                   已有账户？登录
                </div>
            </div>
        </div>
    }
}
