/**
 * Created by Lenovo on 2019/8/18.
 */
//import React from 'react';
import * as React from 'react';
import classnames from 'classnames';
import './index.css';
import {defaultProps} from './defaultProps';
export interface ButtonProps{
    prefixCls?: string;
    onClick?: React.MouseEventHandler<HTMLElement>;
    type: string;
    children?: Array<string>;
    className?: string;
}


export default class Button extends React.Component<ButtonProps, any> {
    static defaultProps = defaultProps;
    constructor(props:any) {
        super(props);
    }
    onClick: React.MouseEventHandler<HTMLButtonElement | HTMLAnchorElement> = e => {
        const { onClick } = this.props;
        if (onClick) {
            (onClick as React.MouseEventHandler<HTMLButtonElement | HTMLAnchorElement>)(e);
        }
    };
    // onClick(e) {
    //     const {onClick} =this.props;
    //     if (onClick) {
    //         (onClick as React.MouseEventHandler<HTMLButtonElement | HTMLAnchorElement>)(e);
    //     }
    // }
    render() {
        const {prefixCls,type,children,className} =this.props;
        switch (type){
            case "primary":
                return(
                    <div className={classnames(prefixCls,className)}>
                        <span className={prefixCls+'-btn'} onClick={this.onClick}>{children}</span>
                    </div>
                );
            case "fill":
                return(
                    <div className={classnames(prefixCls+'-btn-fill',className)} onClick={this.onClick}>{children}</div>
                );
            default:
                return(
                    <div className={classnames(prefixCls,className)}>
                        <span className={prefixCls+'-btn'} onClick={()=>this.onClick()}>{children}</span>
                    </div>
                )
        }
    }
}
