/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import {defaultProps} from './defaultProps';
export default class Type extends React.Component {
    static defaultProps = defaultProps;
    constructor(props) {
        super(props);
        this.scrollLeft = 0;
        this.state = {
            activeType:0//默认选中第一个类型
        }
    }
    //点击类型时触发
    onTypeClick(index,item,e) {
        var el = e.target.getBoundingClientRect();
        console.log(el);
        var offsetLeft = e.target.offsetLeft;
        console.dir(e.target);
        const {typeList} =this.props;
        this.setState({
            activeType:index
        });
        var count = 0;
        var scroll = this.refs.scroll;
        var slideHalf = window.innerWidth/2;
        if(index<=3){
            scroll.scrollLeft = 0;
        }
        if(index>=typeList.length-3){
            scroll.scrollLeft = scroll.scrollWidth;
        }
        if(index>3 && index<typeList.length-3){
            console.log(el.x);
            if(el.x>slideHalf){
                count = 0;
                var timer = setInterval(()=>{
                    if(count <= (el.x-slideHalf)){
                        scroll.scrollTo(this.scrollLeft + count,0);
                        count++;
                    } else {
                        clearInterval(timer)
                    }
                },10);
            }else {
                count = 0;
                console.log("点击回退的距离"+Math.abs(slideHalf-offsetLeft));
                var timer1 = setInterval(()=>{
                    if(count <= Math.abs(offsetLeft-slideHalf)){
                        scroll.scrollTo(this.scrollLeft - count,0);
                        count++;
                    }else {
                        clearInterval(timer1)
                    }
                },10);
            }
            this.scrollLeft = scroll.scrollLeft;
        }
        const {onTypeClick} =this.props;
        if (onTypeClick) {
            onTypeClick(item);
        }
    }
    render() {
        const {prefixCls,typeList} =this.props;
        return(
                <div className={prefixCls} ref="scroll">
                    {typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?prefixCls+"-item-active":prefixCls+"-item"} onClick={(e)=>this.onTypeClick(index,item,e)}>{item.name}</div>
                        )
                    })}
                    </div>
        )
    }
}
