/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import {Title, List, Input,Button} from '../../share';
import {getData, goNext, checkParam} from '../../utils';
import {Switch} from 'antd-mobile';
export default class AddAddress extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            account: '',
            password: '',
            checked:false
        }
        
    }
    change(state) {
        this.setState(state)
    }
    //进入注册页面
    goRegister() {
        goNext(this, 'register');
    }
    login() {
        var arr = [
            {value: this.state.mobile, msg: "请输入手机号"},
            {value: this.state.password, msg: "请输入密码"},
        ]
        checkParam(arr, () => {
            getData({
                method: 'post',
                url: 'login',
                data: {
                    mobile: this.state.mobile,
                    password: this.state.password,
                },
                successCB: (res) => {
                    var token = res.result.token;
                    //登录成功之后将个人信息缓存到本地，并进入首页
                    localStorage.setItem("userInfo", JSON.stringify(res.result));
                    localStorage.setItem("token", token);
                    goNext(this, 'index')
                }
            })
        })
    }

    render() {
        return (
            <div>
                <Title that={this} title="添加收货地址"/>
                <div className="cm-mlr-018">
                    <List>
                        <Input
                            placeholder="收货人"
                            value={this.state.mobile}
                            onChange={(val) => this.change({mobile: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <List>
                        <Input
                            type="number"
                            placeholder="手机号码"
                            value={this.state.password}
                            onChange={(val) => this.change({password: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <List>
                        <Input
                            type="text"
                            placeholder="所在地区"
                            value={this.state.password}
                            onChange={(val) => this.change({password: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <List>
                        <Input
                            type="number"
                            placeholder="详细地址"
                            value={this.state.password}
                            onChange={(val) => this.change({password: val})}
                            style={{textAlign:'left'}}
                        />
                    </List>
                    <div className="cm-flex cm-ptb-018 cm-jc-sb">
                        <div>设为默认地址</div>
                        <Switch
                            checked={this.state.checked}
                            onChange={() => {
                                this.setState({
                                    checked: !this.state.checked,
                                });
                            }}
                        />
                    </div>
                    <Button type="fill"  className="cm-mt-080" onClick={() => this.login()}>保存</Button>
                </div>
            </div>)
    }
}
