/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './message.css';
import avatar from '../../images/personal/avatar.jpg';
import sale from '../../images/personal/sale.png';
import buy from '../../images/personal/buy.png';
import modify from '../../images/personal/modify.png';
import {Title} from '../../share';
import {getData, goNext,getTime} from '../../utils';
export default class MessageList extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            info:[
                {anotherUserName:"我发布的",anotherUserAvatar:[avatar],lastChatContent:"还在吗",updateTime:"2019-09-23 12:09"},
                {anotherUserName:"我卖出的",anotherUserAvatar:[sale],lastChatContent:"要不要",updateTime:"2019-09-23 12:09"},
                {anotherUserName:"我买到的",anotherUserAvatar:[buy],lastChatContent:"还在吗",updateTime:"2019-09-23 12:09"},
                {anotherUserName:"修改密码",anotherUserAvatar:[modify],lastChatContent:"还在吗",updateTime:"2019-09-23 12:09"}
            ]
        }
    }
    componentDidMount(){
        this.goLogin();
    }
    goDetail(item) {
        goNext(this, 'messageDetail',item);
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                console.log(res);
                this.setState({
                    info:res.result.list
                })
            }
        })
    }
    render() {
        return <div>
            <Title title="消息" isHome={true}/>
            <div className="cm-mlr-10">
                {this.state.info.map((item,index)=>{
                    return(
                    <div className="cm-flex cm-ai-c" key={index} onClick={()=>this.goDetail(item)}>
                        <img src={item.anotherUserAvatar} alt="" className="message-img-40 cm-mr-10"/>
                        <div className="cm-flex cm-ai-c cm-jc-sb cm-width-full cm-border-bottom-gary cm-ptb-10">
                        <div>
                            <div className="cm-c-333 cm-fw-bold cm-fs-15">{item.anotherUserName}</div>
                            <div className="cm-c-999 cm-fs-14 cm-mt-05">{item.lastChatContent}</div>
                            <div className="cm-c-999 cm-fs-10 cm-mt-10">{getTime(item.updateTime)}</div>
                        </div>
                            <img src={item.anotherUserAvatar} alt="" className="message-img-60"/>
                        </div>
                    </div>
                    )
                })}
            </div>
        </div>
    }
}
