/**
 * Created by Lenovo on 2019/10/15.
 */
import React from 'react';
import {defaultProps} from './defaultProps';
import './index.css';
import close from '../images/address/close-gray.png';
import checked from '../images/address/checked.png';
export default class Address extends React.Component {
    static defaultProps = defaultProps;
    constructor(props){
        super(props);
        this.selectList = [];
        this.addressList= [
            {name:"湖北省",text:"选择城市",type:"province",
                list:[
                    {name:"武汉市",text:"选择区县",type:"city",
                        list:[
                            {name:"黄陂区",text:"选择街道",type:"district",
                                list:[
                                    {name:"暂不选择",list:[]},
                                    {name:"黄陂街道1",type:"street",list:[]},
                                    {name:"黄陂街道2",type:"street",list:[]}]},
                            {name:"新洲区",text:"选择街道",type:"district",
                                list:[
                                    {name:"暂不选择",list:[]},
                                    {name:"邾城街道1",type:"street",list:[]},
                                    {name:"邾城街道2",type:"street",list:[]}]}
                        ]},
                    {name:"孝感市",text:"选择区县",type:"city",
                        list:[
                            {name:"应城区",text:"选择街道",type:"district",
                                list:[ {name:"暂不选择",list:[]},{name:"三合镇",type:"street",list:[]},]}]}]},
        ]
        this.state={
            defaultText:"选择省份/城市",
            province:"",
            selectOne:"",
            city:"选择城市",
            district:"选择区县",
            street:"选择街道",
            selectList:[],
            hotCity:[
                "北京","上海","广州","深圳","杭州","南京","苏州","天津","武汉","长沙","重庆","成都"
                // {province:"北京",city:"北京",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"上海",city:"上海",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"广州",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"深圳",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"杭州",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"南京",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"苏州",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"天津",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"武汉",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"长沙",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"重庆",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
                // {province:"湖北省",city:"成都",list:[{name:"新洲区",type:"district",text:"选择街道",list:[{name:"张江镇",type:"street",list:[]}]}]},
            ],
            isChoose:false,
            addressList:[
                    {name:"湖北省",text:"选择城市",type:"province",
                        list:[
                            {name:"武汉市",text:"选择区县",type:"city",
                            list:[
                                {name:"黄陂区",text:"选择街道",type:"district",
                                list:[
                                    {name:"暂不选择",list:[]},
                                    {name:"黄陂街道1",type:"street",list:[]},
                                    {name:"黄陂街道2",type:"street",list:[]}]},
                                {name:"新洲区",text:"选择街道",type:"district",
                                 list:[
                                        {name:"暂不选择",list:[]},
                                        {name:"邾城街道1",type:"street",list:[]},
                                        {name:"邾城街道2",type:"street",list:[]}]}
                                        ]},
                            {name:"孝感市",text:"选择区县",type:"city",
                             list:[
                                {name:"应城区",text:"选择街道",type:"district",
                                    list:[ {name:"暂不选择",list:[]},{name:"三合镇",type:"street",list:[]},]}]}]},
            ]
        }
    }
    chooseCity(item){
        for(var i=0;i<this.addressList.length;i++){
            var itemOne = this.addressList[i];
            //同等类型（都是省份或者都是城市）添加后替换掉原先的地区，清除掉后面的选项。
            if(itemOne.type == "city" && itemOne.name == item){
                this.selectList[i] = item;
                this.selectList = this.selectList.slice(0,i+1);
                this.selectList.push({name:item.text,list:item.list});
                break;
            }
        }
        //去重，只添加没有添加的地址
        if(!this.selectList.includes(item)){
            this.selectList.push(item);
            this.selectList.push({name:item.text,list:item.list});
        }
        this.setState({
            selectOne:{name:item.text,list:item.list},
            isChoose:true,
            addressList:item.list,
            defaultText:item.text,
            selectList:this.selectList,
        })
    }
    changeAddress(item){
        this.setState({
            selectOne:item
        });
        //找到对应的数据集
        this.findOne(this.addressList,item)
    }
    findOne(list,item){
        //debugger;
        console.log(this.state.addressList);
        if(list && list.length){
            for(var i=0;i<list.length;i++){
                var item1 = list[i];
                if(item.name.indexOf("选择")>-1){
                    this.setState({
                        addressList:item.list
                    });
                    break;
                }else if(item.name == item1.name){
                    this.setState({
                        addressList:list
                    });
                    break;
                }else {
                    this.findOne(item1.list,item)
                }
            }
        }
    }
    chooseAddress(item){
        console.log(item);
        let {getAddress} = this.props;
        for(var i=0;i<this.selectList.length;i++){
                var itemOne = this.selectList[i];
                //将选择列表表带有选择的去掉。
                if(itemOne.name.indexOf("选择")>-1){
                    this.selectList.splice(i,1);
                    break;
                }
                //同等类型（都是省份或者都是城市）添加后替换掉原先的地区，清除掉后面的选项。
                if(itemOne.type == item.type){
                    this.selectList[i] = item;
                    this.selectList = this.selectList.slice(0,i+1);
                    this.selectList.push({name:item.text,list:item.list});
                    break;
                }
        }
        //去重，只添加没有添加的地址
        if(!this.selectList.includes(item)){
            this.selectList.push(item);
            this.selectList.push({name:item.text,list:item.list});
        }
        this.setState({
            selectOne:{name:item.text,list:item.list},
            isChoose:true,
            addressList:item.list,
            defaultText:item.text,
            selectList:this.selectList,
        },()=>{
            if(item.list.length == 0){
                //过滤掉带有选择的值
                getAddress(this.state.selectList.filter((item)=>item.name && item.name.indexOf("选择")<0));
            }
        })
    }
    render(){
        let {closeModel,prefixCls} = this.props;
        return(
            <div className={prefixCls}>
                <div className={prefixCls+"-wrapper"}>
                <div className={prefixCls+"-title"}>
                    <div className={prefixCls+"-title-left"}></div>
                    <div className={prefixCls+"-title-text"}>请选择</div>
                    <img src={close} alt="" className={prefixCls+"-title-right"} onClick={()=>closeModel()}/>
                </div>
                    {this.state.isChoose?
                        <div className={prefixCls+"-already"}>
                            {this.state.selectList.map((item,index)=>{
                            return <div key={index}
                                       className={item.name == this.state.selectOne.name?prefixCls+"-already-item-active":prefixCls+"-already-item"}
                                       onClick={()=>this.changeAddress(item)}
                            >{item.name}</div>
                        })}
                        </div>:
                        <div className={prefixCls+"-city"}>
                            <div className={prefixCls+"-city-text"}>热门城市</div>
                            <div className={prefixCls+"-city-list"}>
                                {
                                    this.state.hotCity.map((item,index)=>{
                                        return <div key={index} className={prefixCls+"-city-item"} onClick={()=>this.chooseCity(item)}>{item}</div>
                                    })
                                }
                            </div>
                        </div>
                    }
                    <div className={prefixCls+"-select"}>
                            <div className={prefixCls+"-select-text"}>{this.state.defaultText}</div>
                            <div className={prefixCls+"-select-list"}>
                                {
                                    this.state.addressList.map((item,index)=>{
                                        return <div className={prefixCls+"-select-wrapper"} key={index}>
                                        <div onClick={()=>this.chooseAddress(item,index)}
                                             className={item.name == this.state.selectOne.name?prefixCls+"-select-item-active":prefixCls+"-select-item"}>{item.name}</div>
                                            {item.name == this.state.selectOne.name? <img src={checked} alt="" className={prefixCls+"-select-item-checked"}/>:null}
                                        </div>
                                    })
                                }
                            </div>
                    </div>
                </div>
            </div>
        )
    }
}
