/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './message.css';
import avatar from '../../images/personal/avatar.jpg';
import sale from '../../images/personal/sale.png';
import buy from '../../images/personal/buy.png';
import modify from '../../images/personal/modify.png';
import {Title} from '../../share';
import {getData, goNext,getTimeFormat,getTimeHour} from '../../utils';
export default class MessageDetail extends React.Component {
    constructor(props) {
        super(props);
        this.item = this.props.history.location.state ||{};
        console.log(this.item);
        this.count = 0;
        this.state = {
            chatList:[],
            index:0,
            value:null
        }
    }
    componentDidMount(){
        this.goLogin();
            var textarea = document.getElementsByTagName("textarea");
            for(var i=0;i<textarea.length;i++){
                textarea[i].style.height = '3.5rem';
                textarea[i].scrollTop = 0; //防抖动
                textarea[i].style.height = textarea[i].scrollHeight + 'px';
                textarea[i].addEventListener('input',function (e) {
                    e.target.style.height = 'auto';
                    e.target.scrollTop = 0; //防抖动
                    if(e.target.scrollHeight<35){
                        e.target.style.height = '35px';
                    }else {
                        e.target.style.height = e.target.scrollHeight + 'px';
                    }
                })
            }
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                console.log(res);
            }
        })
    }
    onLeftClick(){
        this.props.history.push({pathname:"messageDetail"});
    }
    sendMessage(){
        var value = this.refs.textarea.value;
        if(!value){
            return;
        }
        var user = JSON.parse(localStorage.getItem("userInfo"))||{};
        var userId = user.userId;
        console.log(user);
        var socket = new WebSocket("ws://49.232.24.206:9001/secondary/socket?" + userId);
        this.time = new Date().getTime();
        this.state.chatList.push({content:value,time:this.choseTime()});
        this.setState({
            index:this.state.index++
        },function () {
            this.refs.textarea.value = '';
        });
        var data = {
            "toUserId":user.userId,
            "toUserName":this.item.anotherUserName,
            "toUserAvatar":this.item.anotherUserAvatar,
            "content": value,
        };
        socket.onopen = function() {
            //heartCheck.reset().start(); //心跳检测重置
            console.log("Socket 已打开");
        };
        socket.onmessage = function(msg) {
            console.log(msg);
            if(msg.data.indexOf("{") != -1) {
                console.log(msg);
                var obj = JSON.parse(msg.data);
                console.log(obj);
            }
        };
        if (socket.readyState===1) {
            socket.send(JSON.stringify(data));
        }else{
            //do something
        }

        socket.onerror = function(err) {
            console.log(err);
        };
    }
    changeState(e){
        console.log(e);
        this.setState({
            value:e.target.value
        })
    }
    choseTime(){
        var finalTime = getTimeHour(this.time);
        console.log()
        if((new Date().getTime()) > (this.time+1000*60)){
            finalTime = getTimeHour(new Date().getTime())
        }else {
            if(this.count>0){
                finalTime = '';
            }
        }
        this.count++;
        return finalTime;
    }
    render() {
        return <div>
            <Title title={this.item.anotherUserName} onLeftClick={this.onLeftClick.bind(this)}/>
               <div className="cm-flex cm-ai-c cm-pad-10 cm-bc-white">
                   <img src={this.item.anotherUserAvatar} alt="" className="message-img-60 cm-mr-10"/>
                   <div>
                       <div className="cm-c-main cm-fs-15">￥123</div>
                       <div className="cm-c-999 cm-fs-12 cm-mt-10">交易前聊一聊</div>
                   </div>
               </div>
            {this.state.chatList.map((item,index)=>{
                return  <div className="cm-mar-20-10 cm-flex-column" key={index}>
                    <div className="cm-tx-c">{item.time}</div>
                    <div className="cm-flex cm-ai-c cm-tx-c cm-as-fe cm-mt-10 detail-ml-30">
                        <div className="cm-flex">
                            <div className=" detail-chat-height cm-pad-10 cm-bc-ddd cm-tx-l">{item.content}</div>
                            <div className="detail-triangle"></div>
                        </div>
                        <img src={this.item.anotherUserAvatar} alt="" className="cm-img-large cm-as-fs"/>
                    </div>
                </div>
            })}

               <div></div>
               <div className="detail-position">
                <textarea onChange={(e)=>this.changeState(e)} ref="textarea" className="msg-detail-textarea" name="" id="" cols="30" rows="1" placeholder="想跟他说点什么呢？"></textarea>
                <span className={this.state.value||this.state.value == null?"detail-send":"detail-send-useless"} onClick={()=>this.sendMessage()}>发送</span>
            </div>

        </div>
    }
}
