import React from 'react';
import {Title} from '../../share';
import buy from "../../images/personal/buy.png";
import ProductList from './productList';
import {getData, goNext} from '../../utils';
let self;
export default class MySale extends React.Component {
    constructor(props) {
        super(props);
        self = this;
        this.type = 3;//type表示类型，1.我发布的；2.我卖出的 3.我买到的
        this.state = {
            dataList:[]
        }
    }
    componentDidMount(){
        this.getMyProductList();
    }
    getMyProductList(){
        getData({
            method: 'post',
            url: 'getMyProductList',
            data: {
                type:this.type
            },
            successCB: (res) => {
                this.setState({
                    dataList:res.result.list
                });
            }
        })
    }
    render() {
        return <div>
            <Title title="我买到的" that={this} style={{background:"#fff"}}/>
            <ProductList
                TopArea={TopArea}
                NoData={NoData}
                ButtonArea={ButtonArea}
                dataList = {this.state.dataList}
                self={this}
            />
        </div>
    }
}
class TopArea extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return <div className="cm-ptb-018 cm-p-018 cm-bc-white">
            <div className="cm-flex cm-ai-c">
                <img src={buy} alt="" className="cm-img-04 cm-border-radius-half"/>
                <div className="cm-c-999 cm-ml-009 cm-fs-026">我买到的宝贝共<span className="cm-c-main cm-fs-028"> {this.props.num} </span>件</div>
            </div>
        </div>
    }
}
class ButtonArea extends React.Component{
    constructor(props){
        super(props);

    }
    goDetail(e){
        e.stopPropagation();
        const {item} = this.props;
        goNext(self,"productDetail",item);
    }
    delProduct(e){
        e.stopPropagation();
        const {item} = this.props;
        getData({
            method: 'post',
            url: 'delProduct',
            data: {
                productId:item.id,
                type:self.type
            },
            successCB: (res) => {
                self.getMyProductList();
            }
        })
    }
    render(){
        return  <div className="cm-flex cm-ai-c cm-ptb-018 cm-border-top-eee cm-jc-fe">
            <div className="cm-btn-border-333 cm-flex cm-ai-c cm-mr-018" onClick={(e)=>this.goDetail(e)}>
                <span className="cm-fs-028">查看</span>
            </div>
            <div className="cm-btn-border-333 cm-flex cm-ai-c cm-mr-018" onClick={(e)=>this.delProduct(e)}>
                <span className="cm-fs-028">删除</span>
            </div>
        </div>
    }
}
class NoData extends React.Component{
    constructor(props){
        super(props);
    }
    render(){
        return  <div className="cm-c-666 cm-tx-c cm-mt-080">
            <div>你还没有买到任何宝贝哦！</div>
            <div className="cm-mt-036" onClick={()=>goNext(self,'index')}>
                <span className="cm-btn-main-higher">去首页瞧瞧！</span>
            </div>
        </div>
    }
}
