/**
 * Created by Lenovo on 2019/9/22.
 */
/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import {Title,List} from '../../share';
import {goNext} from '../../utils';
import addr from '../../images/payment/address.png';
import Config from '../../envConfig';
const Item = List.Item;
export default class Order extends React.Component {
    constructor(props) {
        super(props);
        //获取从聊天列表页面和商品详情页传过来的参数
        this.item = this.props.history.location.state || {};
        //获取本地缓存的个人信息
        this.user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
    }
    order(){
        window.location.href = Config.serverUrl+"alipay?orderId="+this.item.orderId+"&token="+this.user.token;
    }
    goModifyInfo(e){
        e.stopPropagation();
        goNext(this,"modifyInfo");
    }
    render() {
        var productImgs = this.item.productImgs && this.item.productImgs.split(",");
        return <div>
            <Title title="购买商品"
                   that={this}
                   style={{background:"#fff"}}
            />
            <div className="" onClick={()=>goNext(this,"productDetail",this.item)}>
                <div className="cm-flex cm-jc-sb cm-bc-white cm-p-018">
                    <div className="cm-flex cm-ai-c">
                        {productImgs && productImgs[0]?
                            <img src={productImgs[0]} alt="" className="cm-img-12 cm-mr-018"/>:
                            <div className="cm-img-10 cm-mr-018 cm-c-999 cm-fs-026 cm-border-ddd cm-flex cm-ai-c cm-tx-c">该商品已下架</div>
                        }
                        <div>
                            <div className="cm-c-333 cm-fs-028">{this.item.productDesc}</div>
                            <div className="cm-c-main cm-fs-028  cm-mt-018 cm-fw-bold">￥{this.item.productPrice}</div>
                        </div>
                    </div>
                </div>
                <div className="cm-mt-018 cm-bc-white cm-plr-018">
                <List onClick={(e)=>this.goModifyInfo(e)}  border={false}>
                    <Item
                        leftTitle="收货地址"
                        leftIcon = {addr}
                        rightTitle={this.user.address}
                    />
                </List>
                </div>
            </div>
                <div className="cm-bottom-position cm-jc-sb">
                    <div className="cm-fs-028  cm-mt-018 cm-fw-bold">付款：<span className="cm-c-main">￥{this.item.productPrice}</span></div>
                    <span className="cm-btn-main-higher cm-as-fe"
                          onClick={() => this.order()}>确定</span>
                </div>
        </div>
    }
}
