/**
 * Created by Lenovo on 2019/9/4.
 */
/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import './personal.css';
import {Title,List,Card} from '../../share';
import publish from "../../images/personal/publish.png";
import chat from '../../images/personal/chat.png';
import {getData, goNext,getTimeFormat,getTimeHour} from '../../utils';
export default class MyPublish extends React.Component {
    constructor(props) {
        super(props);
        this.item = this.props.history.location.state ||{};
        console.log(this.item);
        this.count = 0;
        this.state = {
            dataList:[],
            index:0,
            value:null
        }
    }
    componentDidMount(){
        this.goLogin();
        this.getMyProductList(1);
        var textarea = document.getElementsByTagName("textarea");
        for(var i=0;i<textarea.length;i++){
            textarea[i].style.height = '3.5rem';
            textarea[i].scrollTop = 0; //防抖动
            textarea[i].style.height = textarea[i].scrollHeight + 'px';
            textarea[i].addEventListener('input',function (e) {
                e.target.style.height = 'auto';
                e.target.scrollTop = 0; //防抖动
                if(e.target.scrollHeight<35){
                    e.target.style.height = '35px';
                }else {
                    e.target.style.height = e.target.scrollHeight + 'px';
                }
            })
        }
    }
    goLogin(){
        getData({
            method: 'post',
            url: 'getChatList',
            data: {},
            successCB: (res) => {
                console.log(res);
            }
        })
    }
    changeState(e){
        console.log(e);
        this.setState({
            value:e.target.value
        })
    }
    getMyProductList(){
        getData({
            method: 'post',
            url: 'getMyProductList',
            data: {
                type:1
            },
            successCB: (res) => {
                this.setState({
                    dataList:res.result.list
                })
                console.log(res);
            }
        })
    }
    onLeftClick(){
        this.props.history.goBack()
    }
    modifyPublish(e,item){
        e.stopPropagation();
        goNext(this,"modifyProduct",item);
    }
    render() {
        var user = JSON.parse(localStorage.getItem("userInfo"))||{};
        return <div>
            <Title title="我发布的" onLeftClick={this.onLeftClick.bind(this)}/>
            <div>
                <div className="cm-ptb-018 cm-mlr-018 cm-border-bottom-gary">
                    <div className="cm-flex cm-ai-c" onClick={() => this.goNextPage()}>
                        <img src={publish} alt="" className="cm-img-04 cm-border-radius-half"/>
                        <div className="cm-c-999 cm-ml-018 cm-fs-026">我发布的宝贝共<span className="cm-c-main cm-fs-028"> {this.state.dataList.length} </span>件</div>
                    </div>
                </div>
                <div className="cm-plr-018">
                    {this.state.dataList.length>0?this.state.dataList.map((item,index)=>{
                        var productImgs = item.productImgs && item.productImgs.split(",");
                        return(
                            <Card key={index} onClick={()=>goNext(this,"indexDetail",item.id)}>
                                <div className="cm-flex cm-jc-sb cm-ai-c cm-plr-010 cm-ptb-018">
                                    <div className="cm-flex cm-ai-c">
                                        <img src={productImgs[0]} alt="" className="cm-img-16"/>
                                        <div className="cm-ml-018">
                                            <div className="cm-c-333">{item.productDesc}</div>
                                            <div className="cm-c-main cm-fs-030 cm-mt-018">￥{item.productPrice}</div>
                                            <div className="cm-c-999 cm-pt-018 cm-fs-022">{getTimeFormat(item.createTime)}</div>
                                        </div>
                                    </div>
                                </div>
                                <div className="cm-flex cm-ai-c cm-ptb-018 cm-border-top-eee cm-jc-fe">

                                    <div className="cm-btn-border cm-flex cm-ai-c cm-mr-018" onClick={(e)=>this.modifyPublish(e,item)}>
                                        <span>编辑信息</span>
                                    </div>
                                    <div className="cm-btn-border cm-flex cm-ai-c cm-mr-018" onClick={(e)=>this.modifyPublish(e,item)}>
                                        <span>删除信息</span>
                                    </div>
                                </div>
                            </Card>
                        )
                    }):
                        <div className="cm-c-666 cm-tx-c cm-mt-018">
                            <div>你还没有发布宝贝哦！</div>
                            <div className="cm-mt-040" onClick={()=>goNext(this,'publish')}>
                                <span className="cm-btn-fill">发布宝贝</span>
                            </div>
                        </div>
                    }
                </div>
            </div>
        </div>
    }
}
