import React from 'react';
import {ImagePicker,WhiteSpace,Picker,List} from 'antd-mobile';
import './publish.css';
import add from '../../images/add.png';
import Title from '../../components/Title';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            files:[],
            colorValue: ['#00FF00'],
        }
    }
    onChange = (files, type, index) => {
        console.log(files, type, index);
        this.setState({
            files,
        });
    };
    onTabChange = (key) => {
        console.log(key);
    };
    onChangeColor = (color) => {
        this.setState({
            colorValue: color,
        });
    };
    render(){
        const colorStyle = {
            display: 'inline-block',
            verticalAlign: 'middle',
            width: '16px',
            height: '16px',
            marginRight: '10px',
        };

        const colors = [
            {
                label:
                    (<div>
      <span
          style={{ ...colorStyle, backgroundColor: '#FF0000' }}
      />
                        <span>红色</span>
                    </div>),
                value: '#FF0000',
            },
            {
                label:
                    (<div>
      <span
          style={{ ...colorStyle, backgroundColor: '#00FF00' }}
      />
                        <span>绿色</span>
                    </div>),
                value: '#00FF00',
            },
            {
                label:
                    (<div>
      <span
          style={{ ...colorStyle, backgroundColor: '#0000FF' }}
      />
                        <span>蓝色</span>
                    </div>),
                value: '#0000FF',
            },
        ];
        const { files } = this.state;
        return(
            <div>
                <Title isHome={true} title="商品发布"/>
                <div className="cm-mlr-15 cm-mt-10">
                    <textarea name="" id="" cols="30" className="cm-width-full publish-textarea" rows="10" placeholder="描述商品转手原因，入手渠道和使用感受"></textarea>
                    <ImagePicker
                        files={files}
                        onChange={this.onChange}
                        onImageClick={(index, fs) => console.log(index, fs)}
                        selectable={files.length < 9}
                    />
                </div>
                <div className="cm-line"></div>
                <div className="cm-mlr-15 cm-mt-20">
                    <div className="cm-fw-bold cm-c-333 cm-tx-c cm-fs-20">价格与分类</div>
                    <div className="publish-label-box"><label htmlFor="">价格</label><input type="text" className="publish-input"/></div>
                    <div className="publish-label-box"><label htmlFor="">分类</label><input type="text" className="publish-input"/></div>
                    <Picker
                        className="publish-label-box"
                        data={colors}
                        value={this.state.colorValue}
                        cols={1}
                        onChange={this.onChangeColor}
                    >
                        <List.Item arrow="horizontal">分类</List.Item>
                    </Picker>
                </div>
                <div className="cm-btn cm-mlr-15 publish-mar-40">确认发布</div>
            </div>
        )
    }
}
