/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './register.css';
import {Title,List,Input} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
const Item = List.Item;
export default class Register extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            userName: '',
            mobile: '',
            verifyCode:"",
            password:'',
            confirmPassword:'',
        }
        this.checkArr = [];
    }
    goBack(){
        goNext(this,'login')
    }
    change(state){
        this.setState(state)
    }
    goLogin(){
        goNext(this,'login')
    }
    getVerify(){
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
            },
            successCB:(res)=> {
                this.setState({
                    verifyCode:res.data.verifyCode
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-small"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify()}>获取验证码</span></div>
        )
    }
    register(){
        var arr = [
            {value:this.state.userName,msg:"请输入用户名"},
            {value:this.state.mobile,msg:"请输入手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
            {value:this.state.password,msg:"请输入密码"},
            {value:this.state.confirmPassword,msg:"请输入确认密码"},
            {value:this.state.password == this.state.confirmPassword,msg:"密码与确认密码不一致"},
        ]
        checkParam(arr,()=> {
            getData({
                method:'post',
                url:'register',
                data:{
                    userName:this.state.userName,
                    mobile:this.state.mobile,
                    verifyCode:this.state.verifyCode,
                    password:this.state.password,
                    confirmPassword:this.state.confirmPassword
                },
                successCB:(res)=> {
                    getData({
                        method:'post',
                        url:'login',
                        data:{
                            mobile:this.state.mobile,
                            password:this.state.password
                        },
                        successCB:(res)=> {
                            console.log(res);
                            localStorage.setItem("userInfo",res);
                            //goNext(this,'app',0)
                        }
                    })
                }
            })
        })
    }

    render() {
        return<div>
            <Title  onLeftClick={()=>this.goBack()} title="注册"/>
            <div className="cm-mlr-15 cm-mt-10">
               <List>
                   <Input
                       placeholder="请输入昵称"
                       value={this.state.userName}
                       onChange={(val)=>this.change({userName:val})}
                   />
               </List>
                <List>
                    <Input
                        placeholder="请输入手机号码"
                        value={this.state.mobile}
                        onChange={(val)=>this.change({mobile:val})}
                    />
                </List>
                <List>
                    <Input
                        placeholder="请输入验证码"
                        value={this.state.verifyCode}
                        onChange={(val)=>this.change({verifyCode:val})}
                        renderRight = {()=>this.renderRight()}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入密码"
                        value={this.state.password}
                        onChange={(val)=>this.change({password:val})}
                    />
                </List>
                <List>
                    <Input
                        type="password"
                        placeholder="请输入确认密码"
                        value={this.state.confirmPassword}
                        onChange={(val)=>this.change({confirmPassword:val})}
                    />
                </List>
                    <div className="cm-btn-fill cm-mt-40" onClick={()=>this.register()}>注册</div>
                <div className="cm-tx-r login-operate" onClick={()=>this.goLogin()}>
                   已有账户？登录
                </div>
            </div>
        </div>
    }
}
