/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import praise from '../../images/index/praise.png';
import alreadyPraise from '../../images/index/already-praise.png';
import praiseHeart from '../../images/index/praise-heart.png';
import msg from '../../images/index/msg.png';
import comment from '../../images/index/comment.png';
import {Title} from '../../share';
import {getData,getTime} from '../../utils';
import {Toast} from 'antd-mobile';
import './index.css';
export default class IndexDetail extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            praiseList: [],//点赞列表
            commentList: [],//评论列表
            isPraise:2, //是否点赞，1为是2为否，默认为2
            isShowComment:false,//是否弹出留言框
            item:{}
        };
        this.commentItem = {};
        var id = this.props.history.location.state ||{};
        this.id = id;
    }
    componentDidMount(){
        this.getProductById();
        this.getPraiseList();
        this.getCommentList();
    }
    getProductById(){
        getData({
            method: 'get',
            url: 'getProductDetail?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    item: res.result
                })
            }
        })
    }
    getPraiseList(){
        getData({
            method: 'get',
            url: 'getPraiseList?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    praiseList: res.result.list,
                    isPraise:res.result.praiseStatus
                })
            }
        })
    }
    getCommentList(){
        getData({
            method: 'get',
            url: 'getCommentReplyList?productId='+this.id,
            successCB: (res) => {
                this.setState({
                    commentList: res.result
                })
            }
        })
    }
    showBox(commentItem){
        this.commentItem = commentItem || this.state.item;
        this.setState({
            isShowComment:!this.state.isShowComment
        })
    }
    comment(){
        var userId = this.commentItem.fromUserId;

        if(this.commentItem.fromUserId == this.state.item.publishUserId){
            userId = this.commentItem.toUserId;
        }
        this.setState({
            isShowComment:!this.state.isShowComment
        });
        var value = this.refs.textarea.value;
        getData({
            method: 'post',
            url: 'checkLoginValid',
            successCB: (res) => {
                getData({
                    method: 'post',
                    url: 'commentOrReply',
                    data: {
                        productId:this.id,
                        content:value,
                        toUserId:userId,
                        replyId: this.commentItem.id,
                    },
                    successCB: (res) => {
                        this.getCommentList()
                    }
                })
            }
        });
    }
    praise(){
        var isPraise = (this.state.isPraise == 2)?1:2;
        this.setState({
            isPraise:isPraise
        })
        getData({
            method: 'post',
            url: 'checkLoginValid',
            successCB: (res) => {
                getData({
                    method: 'post',
                    url: 'praiseOrUnPraise',
                    data: {
                        productId:this.id,
                        status:isPraise
                    },
                    successCB: (res) => {
                        this.getPraiseList();
                    }
                })
            }
        });
    }
    onLeftClick(){
        this.props.history.goBack()
    }
    goChat(){
        this.props.history.push("messageDetail",this.state.item)
    }
    render() {
        var productImgs = this.state.item.productImgs && this.state.item.productImgs.split(",")||[];
        return (
            <div>
                <Title title="详情" onLeftClick={this.onLeftClick.bind(this)}/>
                <div className="cm-mlr-018">
                    <div className="cm-flex cm-ai-c cm-border-bottom-gary cm-ptb-018 cm-fs-020">
                        <img src={this.state.item.publishUserAvatar} alt="" className="cm-img-10 cm-mr-018"/>
                        <div>
                        <div className="cm-fw-bold cm-c-333 cm-fs-028">{this.state.item.publishUserName}</div>
                        <div className="cm-c-999 cm-mt-018 cm-fs-020">发布于 {getTime(this.state.item.publishTime)}</div>
                        </div>
                    </div>
                    <div>
                    <div className="cm-ptb-018 cm-fs-026">
                        <div className="cm-c-main">￥{this.state.item.productPrice}</div>
                        <div className="cm-c-333 cm-ptb-018">{this.state.item.productDesc}</div>
                    </div>
                    <div>
                        {productImgs.map((img,index)=>{
                            return(
                                <img src={img} key={index} alt="" className="cm-img-banner cm-mt-018"/>
                                )
                        })}
                    </div>
                    </div>
                    <div className="cm-ptb-018">
                        <div className="detail-line">
                            <span className="cm-mr-018">点赞</span><span>{this.state.praiseList.length}</span>
                        </div>
                        <div className="cm-flex cm-ai-c cm-ptb-018">
                            <img src={praiseHeart} alt="" className="cm-img-04 cm-mr-018"/>
                            <div className="cm-flex cm-ai-c cm-flex-wrap">
                            {this.state.praiseList.length==0?
                                <div>暂无点赞</div>:
                                this.state.praiseList.map((item, index) => {
                                return (
                                    <div className="cm-flex cm-ai-c cm-mr-018" key={index}>
                                        <img src={item.userAvatar} className="cm-img-04 cm-border-radius"/>
                                    </div>
                                )
                            })
                            }
                        </div>
                    </div>
                    </div>
                    <div className="cm-ptb-018">
                        <div className="detail-line"><span
                            className="cm-mr-018">留言</span><span>{this.state.commentList.length}</span></div>
                        <div className="cm-flex cm-ai-c cm-ptb-018">
                            <img src={msg} alt="" className="cm-img-04 cm-mr-018 cm-as-fs cm-mt-018"/>
                            {this.state.commentList.length==0?<div>暂无留言</div>:
                                <div className="cm-width-full">
                                    {this.state.commentList.map((item, index) => {
                                    return (
                                        <div key={index} className="detail-item">
                                        <div className="cm-flex cm-jc-sb cm-ai-c">
                                            <div className="cm-flex cm-ai-c">
                                                <img src={item.fromUserAvatar} alt="" className="cm-img-04 cm-border-radius-half cm-mr-018"/>
                                                <div className="cm-c-333 cm-fw-bold cm-fs-026">{item.fromUserName}</div>
                                            </div>
                                            <div className="cm-c-999 cm-fs-020">{getTime(item.createTime)}</div>
                                        </div>
                                        <div className="cm-c-666 cm-fs-height detail-replay cm-fs-026" onClick={()=>this.showBox(item)}>
                                           {item.type!=1? "回复@"+item.toUserName+"：":null}{item.content}
                                        </div>
                                        </div>
                                    )
                                })}
                                </div>
                            }
                        </div>
                    </div>
                </div>
                    {this.state.isShowComment?<div className="detail-position">
                            <textarea ref="textarea" className="detail-textarea" name="" id="" cols="30" rows="1" placeholder="看对就留言，问问更多细节"></textarea>
                            <div className="detail-send" onClick={()=>this.comment()}>发送</div>
                        </div>:null}
                    <div className="detail-fill">
                    <div className="cm-flex cm-ai-c cm-jc-sb detail-bottom cm-jc-sb">
                        <div className="cm-flex cm-ai-c">
                            <div className="cm-flex cm-ai-c cm-mr-018"><span className="cm-mr-018">点赞</span><img
                                src={this.state.isPraise == 1?alreadyPraise:praise} alt="" className="cm-img-04" onClick={()=>this.praise()}/></div>
                            <div className="cm-flex cm-ai-c"><span className="cm-mr-018">留言</span>
                                <img src={comment} alt="" className="cm-img-04" onClick={()=>this.showBox()}/>
                            </div>
                        </div>
                        <span className="detail-btn" onClick={()=>this.goChat()}>聊一聊</span>
                    </div>
                    </div>

            </div>
        )
    }
}
