/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
import ModifyMobile from './modifyMobile';
const Item = List.Item;
export default class VerifyMobile extends React.Component {
    constructor(props) {
        super(props);
        let user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        console.log(user);
        this.state = {
            getCode:'获取验证码',
            mobile: user.mobile,
            verifyCode:"",
        }
    }
    onLeftClick(){
        this.props.history.goBack();
    }
    change(state){
        this.setState(state)
    }
    getVerify(){
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:3
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode == 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-04"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify()}> {this.state.getCode == "获取验证码"?"获取验证码":this.state.getCode+"秒重新获取"}</span></div>
        )
    }
    modify(opt){
        console.log(opt);
        var arr = [
            {value:opt.mobile,msg:"请输入手机号"},
            {value:opt.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            var param = {
                mobile:opt.mobile,
                verifyCode:opt.verifyCode
            };
            getData({
                method: 'post',
                url: 'verifyOldMobile',
                data: param,
                successCB: (res) => {
                    clearInterval(this.timer);
                    goNext(this,"bindNewMobile");
                    this.setState({
                        getCode:"获取验证码"
                    })
                }
            })
        })
    }
    render() {
        let user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        return<div>
            <ModifyMobile
                 title="验证原手机"
                 mobile = {user.mobile}
                 buttonText = "下一步"
                 submit = {(opt)=>this.modify(opt)}
                 type = {3}
            />
        </div>
    }
}
