/**
 * Created by Lenovo on 2019/10/15.
 */
import React from 'react';
import {defaultProps} from './defaultProps';
import './index.css';
import close from '../images/address/close-gray.png';
export default class Address extends React.Component {
    static defaultProps = defaultProps;
    constructor(props){
        super(props);
        this.state={
            hotCity:["北京","上海","广州","深圳",
                "杭州","南京","苏州","天津",
                "武汉","长沙","重庆","成都"],
            isChoose:false,
            chooseAddress:[],
            addressList:[
                {name:"安徽省",child:[{name:"安庆市",child:[{name:"大观区",child:[{name:"大观开发区",child:[]},{name:"德宽路街道",child:[]}]},{name:"怀宁县",child:[{name:"高河镇",child:[]},{name:"公岭镇",child:[]}]}]},{name:"毫州市",child:[{name:"利辛县",child:[]}]}]},
                {name:"湖北省",child:[{name:"安庆市",child:[{name:"大观区",child:[{name:"大观开发区",child:[]},{name:"德宽路街道",child:[]}]},{name:"怀宁县",child:[{name:"高河镇",child:[]},{name:"公岭镇",child:[]}]}]},{name:"毫州市",child:[{name:"利辛县",child:[]}]}]},
            ]
        }
    }
    chooseCity(item){
        this.setState({
            isChoose:true
        })
    }
    chooseAddress(item){
        let {closeModel} = this.props;
        this.setState({
            addressList:item.child
        },()=>{
            this.state.chooseAddress.push(item.name);
            console.log(this.state.chooseAddress);
            if(item.child.length == 0){
                closeModel();
            }
        })
    }
    render(){
        let {closeModel,prefixCls} = this.props;
        return(
            <div className={prefixCls}>
                <div className={prefixCls+"-wrapper"}>
                <div className={prefixCls+"-title"}>
                    <div className={prefixCls+"-title-left"}></div>
                    <div className={prefixCls+"-title-text"}>请选择</div>
                    <img src={close} alt="" className={prefixCls+"-title-right"} onClick={()=>closeModel()}/>
                </div>
                    {this.state.isChoose?<div className={prefixCls+"-already"}>
                            <div className={prefixCls+"-already-item"}>湖北省</div>
                            <div className={prefixCls+"-already-item"}>武汉</div>
                            <div className={prefixCls+"-already-item"}>请选择县</div>
                        </div>:
                        <div className={prefixCls+"-city"}>
                            <div className={prefixCls+"-city-text"}>热门城市</div>
                            <div className={prefixCls+"-city-list"}>
                                {
                                    this.state.hotCity.map((item,index)=>{
                                        return <div key={index} className={prefixCls+"-city-item"} onClick={()=>this.chooseCity(item)}>{item}</div>
                                    })
                                }
                            </div>
                        </div>
                    }
                    <div className={prefixCls+"-select"}>
                            <div className={prefixCls+"-select-text"}>选择省份/地区</div>
                            <div className={prefixCls+"-select-list"}>
                                {
                                    this.state.addressList.map((item,index)=>{
                                        return <div key={index} onClick={()=>this.chooseAddress(item)} className={prefixCls+"-select-item"}>{item.name}</div>
                                    })
                                }
                            </div>
                    </div>
                </div>
            </div>
        )
    }
}
