/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './login.css';
import account from '../../images/login/account.png';
import password from '../../images/login/password.png';
import defaultAvatar from '../../images/login/avatar.jpg';
import {Title,List,Input} from '../../share';
import {getData, goNext,checkParam} from '../../utils';
import {Toast} from 'antd-mobile';
export default class Publish extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            account: '',
            password: '',
        }
    }
    goBack(){
        goNext(this,'app',3)
    }
    change(state){
        this.setState(state)
    }
    goRegister(){
        goNext(this,'register');
    }
    login() {
        var arr = [
            {value: this.state.mobile, msg: "请输入手机号"},
            {value: this.state.password, msg: "请输入密码"},
        ]
        checkParam(arr, () => {
            getData({
                method: 'post',
                url: 'login',
                data: {
                    mobile: this.state.mobile,
                    password: this.state.password,
                },
                successCB: (res) => {
                    Toast.success(res.msg, 1);
                    localStorage.setItem("userInfo",JSON.stringify(res));
                    goNext(this,'app',0)
                }
            })
        })
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.goBack()} title="登录"/>
        <div className="cm-mlr-15">
            <div className="login-margin-40"><img src={defaultAvatar} alt="" className="login-avatar cm-border-radius"/></div>
            <List>
                <Input
                    src={account}
                    placeholder="请输入账号"
                    value={this.state.mobile}
                    onChange={(val)=>this.change({mobile:val})}
                />
            </List>
            <List>
                <Input
                    type="password"
                    src={password}
                    placeholder="请输入密码"
                    value={this.state.password}
                    onChange={(val)=>this.change({password:val})}
                />
            </List>
                <div className="cm-btn-fill login-mt-40" onClick={()=>this.login()}>登录</div>
            <div className="cm-tx-c login-operate">
                <span onClick={()=>this.goRegister()}>快速注册</span>
                <span>|</span>
                <span>忘记密码?</span>
            </div>
        </div>
        </div>
    }
}
