/**
 * Created by Lenovo on 2019/8/10.
 */
import React from 'react';
import './personal.css';
import {Title,List,Input,Button} from '../../share';
import {getData, goNext,isDefine,checkParam} from '../../utils';
import {Toast,ImagePicker} from 'antd-mobile';
import verifyCode from '../../images/register/verify-code.png';
const Item = List.Item;
export default class ModifyInfo extends React.Component {
    constructor(props) {
        super(props);
        let user = localStorage.getItem("userInfo");
        user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        console.log(user);
        this.state = {
            userAvatar:[user.userAvatar],
            getCode:'获取验证码',
            userName: user.userName,
            mobile: user.mobile,
            verifyCode:"",
        }
    }
    componentDidMount(){
       this.uploadImg();
    }
    onLeftClick(){
        this.props.history.goBack();
    }
    change(state){
        this.setState(state)
    }
    getVerify(){
        console.log(this.state.mobile);
        if(!isDefine(this.state.mobile)){
            Toast.info("请输入手机号");
            return;
        }
        getData({
            method:'post',
            url:'getVerifyCode',
            data:{
                mobile:this.state.mobile,
                type:1
            },
            successCB:(res)=> {
                this.setState({
                    getCode:60
                },()=>{
                    this.timer = setInterval(()=>{
                        //验证码过了60秒，消除定时器
                        if(this.state.getCode == 1){
                            clearInterval(this.timer);
                            this.setState({
                                getCode:"获取验证码"
                            })
                        }else {
                            //验证码60秒倒计时
                            this.setState({
                                getCode:--this.state.getCode
                            })
                        }
                    },1000)
                })
            }
        })
    }
    renderRight(){
        return(
            <div className="cm-flex cm-ai-c"><img src={verifyCode} alt="" className="cm-img-04"/><span className="cm-c-main register-verify" onClick={()=>this.getVerify()}> {this.state.getCode}</span></div>
        )
    }
    modify(){
        var arr = [
            {value:this.state.userAvatar,msg:"请选择头像"},
            {value:this.state.userName,msg:"请输入用户名"},
            {value:this.state.mobile,msg:"请输入手机号"},
            {value:this.state.verifyCode,msg:"请输入验证码"},
        ]
        checkParam(arr,()=> {
            console.log(this.state.userAvatar);
            var token = localStorage.getItem("token");
            let param = new FormData(); //创建form对象
            param.append('userAvatar',this.state.userAvatar[0]);//通过append向form对象添加数据
            param.append('userName',this.state.userName);
            param.append('mobile',this.state.mobile);
            param.append('verifyCode',this.state.verifyCode);
            getData({
                method: 'post',
                url: 'updateUserInfo',
                headers:{
                    'Content-Type':'multipart/form-data',
                    'token':token
                },
                data: param,
                successCB: (res) => {
                    getData({
                        method:'post',
                        url:'login',
                        data:{
                            mobile:this.state.mobile,
                            password:this.state.password
                        },
                        successCB:(res)=> {
                            localStorage.setItem("userInfo",JSON.stringify(res.result));
                            goNext(this,'app',0)
                        }
                    })
                }
            })
        })
    }
    getAvatar(event){
        var files = event.target.files;
        this.setState({
            userAvatar:files
        })
        if(window.FileReader) {
            var file = files[0];
            var fr = new FileReader();
            fr.onloadend = (e)=> {
                this.refs.avatar.style.backgroundImage  = "url("+e.target.result+")";
                this.refs.avatar.style.backgroundSize = "100%";
            };
            fr.readAsDataURL(file);  //也是利用将图片作为url读出

        }
        this.refs.avatar.setAttribute("class","register-again");

    }
    uploadImg(e) {
        let user = localStorage.getItem("userInfo");
        user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        console.log(user);
        var reader = new FileReader();
        //reader.readAsDataURL(file);
        reader.onload = function (e) {
            this.refs.avatar.style.backgroundImage  = "url("+user.userAvatar+")";
            this.refs.avatar.style.backgroundSize = "100%";
        }
    }
    render() {
        return<div>
            <Title  onLeftClick={()=>this.onLeftClick()} title="编辑个人信息"/>
            <div className="register-position">
                <div className="register-input-box" ref="avatar">
                    <input type="file" className="register-input"  onChange={(e)=>this.getAvatar(e)}/>
                </div>
            </div>
            <div className="cm-mlr-018 cm-mt-018">
                <List>
                    <Input
                        label="昵称"
                        style = {{textAlign:"right"}}
                        maxLength={16}
                        placeholder="请输入昵称"
                        defaultValue={this.state.userName}
                        onChange={(val)=>this.change({userName:val})}
                        className="cm-c-999"
                    />
                </List>
                <List onClick={()=>goNext(this,"verifyMobile")}>
                    <Item
                        leftTitle="手机号"
                        rightTitle={18209497889}
                    />
                </List>
                {/*<List>*/}
                    {/*<Input*/}
                        {/*placeholder="请输入验证码"*/}
                        {/*defaultValue={this.state.verifyCode}*/}
                        {/*onChange={(val)=>this.change({verifyCode:val})}*/}
                        {/*renderRight = {()=>this.renderRight()}*/}
                    {/*/>*/}
                {/*</List>*/}
                <Button type="fill" className="cm-mt-040" onClick={() => this.modify()}>确认修改</Button>
            </div>
        </div>
    }
}
