/**
 * Created by Lenovo on 2019/8/7.
 */
import axios from "axios";
import Config from './envConfig';
import {Toast} from 'antd-mobile';
import createHistory from 'history/createBrowserHistory';
const history = createHistory();
export function getData(obj){
    var token = localStorage.getItem("token");
    if(obj.isShowLoad){
        Toast.loading('加载中...', 0);
    }
    var pro =  "";
    if(obj.method == "get"){
        pro = axios.get(Config.serverUrl+obj.url,{
            headers:obj.headers||{
                token:token,
                requestId:"66",
            }
        })
    }else {
        console.log(obj.headers);
        pro =  axios({
            method: obj.method,
            url: Config.serverUrl+obj.url,
            data: obj.data,
            headers:obj.headers||{
                token:token,
                requestId:'11'
            }
        });
    }
    pro.then(function (res) {
        let result = res.data;
        console.log(result);
        if(result.code == 999){
            console.log(history);
            history.go("login");
            //goNext(this,"login");
        }
        if(obj.successCB){
            Toast.hide();
            console.log(res);
            if(result.code == 0){
                obj.successCB(result);
            }else {
                Toast.fail(result.message);
            }
        }
    }).catch(function (err) {
        if(obj.failCB){
            obj.failCB(err);
        }
    });
}
export function isDefine(str) {
     if(str == null || str == '' || str == undefined || str == NaN){
         return false
     };
     return true;
}

export function goNext(that,pathname,state) {
    console.log(that);
    if(state ||state==0){
        that.props.history.push({pathname:pathname,state:state});
    }else {
        that.props.history.push(pathname);
    }
}
export function prompt(value,msg) {
    if(!isDefine(value)){
        Toast.info(msg, 1);
        return false
    }else {
        return true
    }
}
export function  *verify(arr){
    for(var i=0;i<arr.length;i++){
        yield prompt(arr[i].value,arr[i].msg);
    }
    //Generator 函数需要return之后才算真正结束
    return;
}
//通过递归执行分段函数
export function doNext(bool,cb){
    //先执行第一个分段函数
    var x= bool.next();
    if(x.value){
        //当 value 值为 true 时（也就是非空的时候），再执行下一个分段函数
        doNext(bool,cb);
    }else {
        if(x.done && cb){
            //分段函数执行完毕之后执行回调
            cb();
        }
    }
}
export function checkParam(arr,cb){
    //Generator 函数需要调用一次才能执行（后面通过 next 执行）。
    var bool = verify(arr);
    doNext(bool,cb);
}
