/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import backBlock from '../image/title/back-block.png';
export default class Type extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            activeType:0
        }
    }
    onTypeClick(index,item) {
        this.setState({
            activeType:index
        })
        const {onTypeClick} =this.props;
        if (onTypeClick) {
            onTypeClick(item);
        }
    }
    render() {
        const {typeList} =this.props;
        return(
            <div>
                <div className="type-box" ref="box">
                    {typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?"index-type-active":"index-type"} onClick={()=>this.onTypeClick(index,item)}>{item.name}</div>
                        )
                    })}
                </div>
            </div>
        )
    }
}
