import React from 'react';
import Index from './pages/index/index';
import './App.css';
export default class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            children:[
                {label:"首页",child:Index},
                {label:"发布",child:null},
                {label:"消息",child:null},
                {label:"我的",child:null},
            ],
            activeKey:0
        }
    }
    changeTab(index){
        this.setState({
            activeKey:index
        })
    }
    render() {
        return (
            <div>
                <div className="tabs">
                    <div className="line" ref="line"></div>
                    {this.state.children.map((item,index)=>{
                        return(<div style={{color:index==this.state.activeKey?'red':''}} onClick={()=>this.changeTab(index)}>
                            {item.label}
                        </div>)
                    })}
                </div>
                {this.state.children.map((item,index)=>{
                    if(index == this.state.activeKey){
                        return(<div>{item.child}</div>)
                    }else {
                        return null
                    }
                })}
            </div>
        )
    }
}
