/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import search from '../image/search/search.png';
import close from '../image/search/close.png';
export default class Search extends React.Component {
    static defaultProps = defaultProps;

    constructor(props) {
        super(props);
        this.state = {
            isShowClose: false,//是否显示搜索框右边关闭按钮
        }
    }

    componentDidMount() {

    }

    leftClick() {
        const {leftClick} =this.props;
        var key = this.refs.input.value;
        if (leftClick) {
            leftClick(key);
        }
    }

    rightClick() {
        const {rightClick} =this.props;
        if (rightClick) {
            rightClick();
        }
    }

    //点击搜索框右边关闭图标时，清楚搜索内容并删除图标
    closeValue() {
        this.refs.input.value = '';
        this.setState({
            isShowClose: false
        });
    }

    //点击搜索框进行关键字搜索时，出现右边关闭图标
    onChange(event) {
        if (event.target.value == '') {
            this.setState({
                isShowClose: false
            })
        } else {
            this.setState({
                isShowClose: true
            })
        }
    }

    render() {
        const {
            prefixCls,
            searchIcon,
            leftClick,
            rightIcon,
            placeholder
        } =this.props;
        return (
            <div className="search-fill">
                <div className="search-layout">
                    <div className="search-content">
                        <img src={searchIcon ? searchIcon : search} alt="" className="search-left-icon"
                             onClick={() => this.leftClick()}/>
                        <input type="text" ref="input" className="search-input" placeholder={placeholder}
                               onChange={(e) => this.onChange(e)}/>
                        {this.state.isShowClose ?
                            <img src={close} alt="" className="search-close-icon"
                                 onClick={() => this.closeValue()}/> : null
                        }
                    </div>
                    <img src={rightIcon} alt="" className="search-right-icon" onClick={() => this.rightClick()}/>
                </div>
            </div>
        )
    }
}
