/**
 * Created by Lenovo on 2019/8/4.
 */
import React from 'react';
import search from '../../images/index/search.png';
import publish from '../../images/index/publish.png';
import close from '../../images/index/close.png'
import {Carousel,ListView} from 'antd-mobile';
import axios from "axios";
import {getData,goNext} from '../../utils';
import './index.css';
export default class Index extends React.Component {
    constructor(props){
        super(props);
        this.initData = [];//定义空数组，用于上拉加载更加时拼接数据
        this.id="";
        this.key="";
        const dataSource = new ListView.DataSource({
            rowHasChanged: (row1, row2) => row1 !== row2,
        })//定义初始化数据源
        this.state = {
            isLoading: true,//用来控制是否显示loading
            dataSource: dataSource.cloneWithRows(this.initData),
            pageNum:1,//定义初始化请求序号
            pageSize:10,//定义初始化请求数量
            typeList:[{name:"全部"}],//定义初始化产品类型
            activeType:0,//定义初始化产品类型选中状态
            productList:[],//定义初始化产品数组
            hasMore:true,//是否已经加载完成
            bannerList:[],//定义初始化轮播图数组
            isShowClose:false,//是否显示搜索框右边关闭按钮
            statusText:"加载中"//上拉加载判断状态
        }
    }
    //选择商品类型
    changeType(index,id){
        this.id = id;
        var box = this.refs.box;
        var eleWidth = 50;//子元素宽度
        box.style.width = this.state.typeList.length * eleWidth+"px";
        var offsetStartIndex  = 3;//从下标为3的元素开始偏移
        var offsetWidth = (this.state.typeList.length * eleWidth - window.innerWidth)/4;
        // offsetWidth 为偏移量，计算公式为：（每个元素宽度*元素个数）/偏移元素个数。
        if(index >=offsetStartIndex && index<= this.state.typeList.length - offsetStartIndex){
            box.style.transform = "translateX("+(-(index-(offsetStartIndex-1))*offsetWidth)+"px)";
            box.style.transition = "transform .5s";
        };
        this.state.pageNum = 1;
        this.initData = [];
        this.setState({
            dataSource: this.state.dataSource.cloneWithRows(this.initData),
            isLoading: false,
            hasMore:true,
            activeType:index,
            statusText:"加载中"
        })
        this.getProductList();
    }
    //点击某一商品进入商品详情
    goDetail(item){
        goNext(this,"indexDetail",item);
    }
    //点击进入发布页面
    goPublish(e){
        goNext(this,"app",1);
    }
    //获取商品列表
    getProductList(){
        getData({
                method: 'post',
                url: 'getProductList',
                isShowLoad:true,
                data:{
                    pageNum:this.state.pageNum,
                    pageSize:this.state.pageSize,
                    productDesc:this.key,
                    productTypeId:this.id
                },
                successCB: (res) => {
                    this.state.pageNum++;
                    this.initData = this.initData.concat(res.result.list);
                    let total = res.result.total;
                    if(total == 0){
                        this.setState({
                            statusText:"暂无数据"
                        })
                    }else if(total == this.initData.length){
                        this.setState({
                            dataSource: this.state.dataSource.cloneWithRows(this.initData),
                            hasMore:false,
                            statusText:"加载完成"
                        })
                    }else {
                        setTimeout(()=>{
                            this.setState({
                                dataSource: this.state.dataSource.cloneWithRows(this.initData),
                                isLoading: false,
                            });
                        },600)

                    }
                }
        })
    }
    //获取轮播图列表
    getBannerList(){
        getData({
                method: 'get',
                url: 'getBannerList',
                successCB: (res) => {
                    this.setState({
                        bannerList: res.result
                    })
                }
        })
    }
    //获取商品类型列表
    getProductTypeList(){
        getData({
            method: 'get',
            url: 'getProductTypeList',
            successCB: (res) => {
                localStorage.setItem("typeList",JSON.stringify(res.result));
                let typeList = this.state.typeList.concat(res.result);
                this.setState({
                    typeList: typeList
                })
            }
        })
    }
    //通过商品关键字搜索
    getProductListByKey(){
        this.state.pageNum = 1;
        this.initData = [];
        this.setState({
            dataSource: this.state.dataSource.cloneWithRows(this.initData),
            isLoading: false,
            hasMore:true,
            statusText:"加载中"
        })
        var key = this.refs.input.value;
        this.key = key;
        this.getProductList()
    }
    //加载页面时获取轮播图列表、商品类型列表、商品列表
    componentDidMount(){
        this.getProductList();
        this.getProductTypeList();
        this.getBannerList();
    }
    //上拉加载更多时触发
    onEndReached = (event) => {
        console.log("isLoading"+this.state.isLoading);
        console.log("hasMore"+this.state.hasMore);
        if (this.state.isLoading ) {
            return false;
        }
        if (!this.state.hasMore) {
            return false;
        }
        this.setState({ isLoading: true},()=>{
            this.getProductList();
        });
    }
    onScroll(e){
        // console.dir(document.body);
        // console.log(e);
    }
    //点击搜索框右边关闭图标时，清楚搜索内容并删除图标
    closeValue(){
        this.refs.input.value = '';
        this.setState({
            isShowClose:false
        });
    }
    //点击搜索框进行关键字搜索时，出现右边关闭图标
    onChange(){
        this.setState({
            isShowClose:true
        })
    }
    //渲染页面
    render() {
        const row = (rowData, sectionID, rowID) => {
            var productImgs = rowData.productImgs.split(",");
            return (
                    <div  className={rowID%2==0?"index-product-right":"index-product-left"} key={rowID} onClick={()=>this.goDetail(rowData)}>
                        <div className="index-img">
                            <img src={productImgs[0]} alt="" className="index-img"/>
                        </div>
                        <div className="index-desc">{rowData && rowData.productDesc}</div>
                        <div className="index-price">￥{rowData && rowData.productPrice}</div>
                    </div>
            );
        };
        return (
            <div>
                <div className="index-top">
                    <div className="cm-flex cm-width-full">
                        <img src={search} alt="" className="index-search" onClick={()=>this.getProductListByKey()}/>
                        <input type="text"  ref="input" className="index-input" placeholder="请输入商品关键字"/>
                        {this.state.isShowClose?
                            <img src={close} alt="" className="index-close" onClick={()=>this.closeValue()}/>:null
                        }
                    </div>
                    <img src={publish} alt="" className="cm-img-mid" onClick={(e)=>this.goPublish(e)}/>
                </div>
                <div className="cm-mlr-15">
                    <div className="index-carousel">
                        {this.state.bannerList? <Carousel>
                              {this.state.bannerList.map(item => (
                                  <img src={item.image} alt="" className="cm-img-banner index-banner"/>
                              ))}
                            </Carousel>:null
                        }
                    </div>

                    <div className="cm-flex cm-ai-c index-scroll" ref="box">
                    {this.state.typeList && this.state.typeList.map((item,index)=>{
                        return(
                            <div key={index} className={this.state.activeType == index?"index-type-active":"index-type"} onClick={()=>this.changeType(index,item.id)}>{item.name}</div>
                        )
                    })}
                     </div>
                </div>
                    <ListView
                        useBodyScroll={true}
                        ref={el => this.lv = el}
                        dataSource={this.state.dataSource}
                        renderFooter={() => (<div style={{ paddingBottom: 60, textAlign: 'center' }}>
                            {this.state.statusText}
                        </div>)}
                        renderBodyComponent={() => <Body/>}
                        renderRow={row}
                        pageSize={4}
                        onScroll={(e) => this.onScroll(e)}
                        scrollRenderAheadDistance={500}
                        onEndReached={this.onEndReached}
                        onEndReachedThreshold={10}
                    />

            </div>
        )
    }
}
function Body(props) {
    return(
        <div>
            <div>
                {props.children}
            </div>
        </div>
    )
}
