/**
 * Created by Lenovo on 2019/9/22.
 */
/**
 * Created by Lenovo on 2019/9/2.
 */
import React from 'react';
import {Title,List} from '../../share';
import {goNext} from '../../utils';
import Config from '../../envConfig';
const Item = List.Item;
export default class OrderDetail extends React.Component {
    constructor(props) {
        super(props);
        //获取从聊天列表页面和商品详情页传过来的参数
        this.item = this.props.history.location.state || {};
        //获取本地缓存的个人信息
        this.user = localStorage.getItem("userInfo")?JSON.parse(localStorage.getItem("userInfo")):{};
        this.state = {
            orderList:[
                {title:"买家昵称",value:"zhangsan"},
                {title:"订单编号",value:"125"},
                {title:"交易时间",value:"2019-10-01 09:30"}
            ]
        }
    }
    order(){
        window.location.href = Config.serverUrl+"alipay?orderId="+this.item.orderId+"&token="+this.user.token;
    }
    close(){

    }
    render() {
        var productImgs = this.item.productImgs && this.item.productImgs.split(",")||[];
        return <div className="cm-bc-white">
            <Title title="订单信息"
                   that={this}
                   style={{background:"#fff"}}
                   onLeftClick={()=>goNext(this,"index")}
            />
                <div className="cm-c-999 cm-pt-018 cm-tx-c">
                    <span className="cm-fs-040 cm-fw-bold">{this.item.productPrice||9.9}</span>
                <span>元</span>
                    <div className="cm-fs-024">未付款</div>
                </div>
                <div className="cm-tx-c cm-mtb-036 cm-fs-024"><span className="cm-c-main">0</span>天
                    <span className="cm-c-main">23</span>时
                    <span className="cm-c-main">59</span>分后，如果您未付款，订单将自动关闭
                </div>
                <div className="cm-p-018 cm-border-bottom-eee cm-border-top-eee">
                    <div className="cm-mtb-009 cm-fs-024 cm-c-666"><span className="cm-fs-028 cm-c-333 cm-fw-bold">张三 </span><span> 18209603995</span></div>
                    <div className="cm-fs-024 cm-c-666">上海上海市浦东新区金牛路1号1001室</div>
                </div>
                <div className="cm-flex cm-jc-sb cm-bc-white cm-border-bottom-eee cm-p-018 cm-ai-c">
                    <div className="cm-flex cm-ai-c">
                        <img src="http://secondary-test.oss-cn-shanghai.aliyuncs.com/images/product/20191011/1570805210026_O1CN01BnfTZA2MxxcNjITxe_!!0-item_pic.jpg_430x430q90.jpg" alt="" className="cm-img-06 cm-mr-018"/>
                        <div className="cm-c-333 cm-fs-026 cm-fs-ellipsis" style={{width:'220px'}}>{this.item.productDesc||"老家种的橙子，甘甜又可口，快来尝尝吧。"}</div>
                    </div>
                    <div className="cm-c-main">联系卖家</div>
                </div>
            <div>
                {this.state.orderList.map((item,index)=>{
                    return  <div key={index} className="cm-mlr-018 cm-ptb-009 cm-fs-024 cm-c-999 cm-flex cm-jc-sb">
                        <div>{item.title}</div>
                        <div>{item.value}</div>
                    </div>
                })}
            </div>
            <div className="cm-bottom-position cm-jc-sb">
                <div className="cm-mr-018 cm-ptb-018 cm-w-full cm-tx-c cm-bc-666 cm-c-white cm-border-radius-10"
                      onClick={() => this.close()}>关闭交易</div>
                <div className="cm-ptb-018 cm-w-full cm-tx-c cm-bc-main cm-c-white cm-border-radius-10"
                      onClick={() => this.order()}>我要付款</div>
            </div>
        </div>
    }
}
