/**
 * Created by Lenovo on 2019/8/18.
 */
import React from 'react';
import './index.css';
import search from '../image/search/search.png';
import close from '../image/search/close.png';
export default class Search extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isShowClose:false,//是否显示搜索框右边关闭按钮
        }
    }
    componentDidMount(){
        console.log(this);
    }
    onLeftClick(){
        const {leftClick} =this.props;
        if(leftClick){
            leftClick();
        }
    }
    rightClick(){
        const {rightClick} =this.props;
        if(rightClick){
            rightClick();
        }
    }
    //点击搜索框右边关闭图标时，清楚搜索内容并删除图标
    closeValue(){
        this.refs.input.value = '';
        this.setState({
            isShowClose:false
        });
    }
    //点击搜索框进行关键字搜索时，出现右边关闭图标
    onChange(){
        this.setState({
            isShowClose:true
        })
    }
    render() {
        const {searchIcon,leftClick,rightIcon,rightClick,placeholder} =this.props;
        return(
            <div className="index-full">
                <div className="index-top">
                    <div className="cm-flex cm-width-full">
                        <img src={searchIcon?searchIcon:search} alt="" className="index-search" onClick={()=>this.onLeftClick()}/>
                        <input type="text"  ref="input" className="index-input" placeholder={placeholder} onChange={()=>this.onChange()}/>
                        {this.state.isShowClose?
                            <img src={close} alt="" className="index-close" onClick={()=>this.closeValue()}/>:null
                        }
                    </div>
                    <img src={rightIcon} alt="" className="cm-img-mid" onClick={()=>this.rightClick()}/>
                </div>
            </div>
            )
    }
}
